css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#ff-controls toolbarbutton{
	-moz-appearance: none!important;
	-moz-box-align: stretch;
	-moz-box-pack: start;
	background: none transparent!important;	
	border: none;
	padding: 0px;
	margin: 0px;
	z-index: 500;
}

#main-window[sizemode='normal'] #ff-controls{	
margin:-8px 3px 0px 0px !important;
}

#main-window[sizemode='maximized'] #ff-controls{	
margin:-12px 0px 0px 0px !important;
}

#ff-minimize-button{
  list-style-image: url("data:image/png;base64,") !important;
   -moz-image-region: rect(0px 27px 18px 0px)!important;

}
#ff-minimize-button:hover {
  -moz-image-region: rect(0px 54px 18px 27px)!important;
}
#ff-minimize-button:active {
  -moz-image-region: rect(0px 81px 18px 54px)!important;
}

#ff-maximize-button{
  list-style-image: url("data:image/png;base64,%3D%3D") !important;
 -moz-image-region: rect(0px 25px 18px 0px)!important;
}

#ff-maximize-button:hover{
  -moz-image-region: rect(0px 50px 18px 25px)!important;
}
#ff-maximize-button:hover:active{
  -moz-image-region: rect(0px 75px 18px 50px)!important;
}

#main-window[winstate="1"] #ff-maximize-button{
  list-style-image: url("data:image/png;base64,") !important;
}

#ff-close-button{
  list-style-image: url("data:image/png;base64,%3D%3D") !important;
  -moz-image-region: rect(0px 46px 18px 0px)!important;
opacity:.8;
}
#ff-close-button:hover{
  -moz-image-region: rect(0px 92px 18px 46px)!important;
opacity:1;  
}
#ff-close-button:hover:active {
  -moz-image-region: rect(0px 138px 18px 92px)!important;
opacity:1;
}


#ff-fullscreen-button{
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: rect(0px 16px 16px 0px)!important;
}

#ff-fullscreen-button:hover {
  -moz-image-region: rect(0px 32px 16px 16px)!important;
}
#ff-fullscreen-button:hover:active {
  -moz-image-region: rect(0px 48px 16px 32px)!important;
}
