@-moz-document domain("service.gmx.net"), domain("web.de") {


/*Remove Google search and help button*/
/*Entfernt Google-Suche und Hilfeknopf*/
LI#app_help,
DIV#box_search
{display: none !important;}

/*Move Logout button to right again*/
/*Logout Knopf wieder nach rechts bewegen*/

LI#link_logout
{float:right !important;
margin-left: 35px!important;
}



/*clean upper left corner*/
/*in oberer linken Ecke Logo entfernen*/
/*gmx*/
A#portal,
A#button_more.portal.portal-toggle,
#portal_popup,
/*web.de*/
DIV.button.portal
{display: none !important;}



/*Put new image in upper left corner*/
/*Neues Bild in Ecke setzen*/

DIV#box_portal{
background-image:url("https://images.gmx.net/images/outsource/application/organizer/de/gmxde/gmxde/de/img/logo.png");
width:135px!important;
margin-left: 5px!important;
margin-top: 5px!important;

/*Alternative Image
background-image:url("https://images.gmx.net/images/gmx/logo-mc.jpg");
width:140px!important;
margin-left: -14px!important;
margin-top: -1px!important;
*/
}



/* remove unwanted icons */
/* unerw체nschte Icons entfernen */

/*LI#app_riamail,*//*GMX Mail 2010*/

LI#app_messenger,
LI#app_rssreader,
LI#app_bookmarks,
LI#app_notes,
LI#login_facebook,
LI#app_demail,
LI#login_hybridmail,
DIV#box_cockpit,

/*GMX FreeMail-Icons, if any*/
LI#app_ebay,
DIV#iframe_promo
{display: none !important;}



/* center email icon and narrow bar around addressbook */
/* Zentriere Email-Icon und verschm채lere Leiste beim Adressbuch */
DIV.ui-pager
{width:82px !important;
margin-left:-00px !important;
}
DIV.ui-pager-content
{width:37px !important;}
LI#app_mail
{margin-left:25px !important;
width:32px !important;
}


}
