@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rememberthemilk.com") {

body,input,select,textarea,button {
	font-family: "Segoe UI" ,Tahoma,Verdana,Arial!important;
	font-size: .8em!important;
	color: #fff!important;
	text-align: left!important
}

.notewidth { width: 98%!important }

.noteedittext { margin-bottom: 10px!important }

h1 {
	color: #9F9F9F!important;
	font-variant: small-caps!important
}

input,select,textarea,button { font-size: 1.1em!important; color: #000!important }

label { margin: 3px!important }

/*------- inputs ----------------*/
input[type="submit"],input[type="reset"],td.field input[type="button"],div.roundbtn_content, div.gobtn_content {
	-moz-appearance: none!important;
	border: 1px solid #77736B !important;
	color: #EEE!important;
	cursor: pointer;
	font-weight: 400!important;
	height: 24px!important;
	padding: 0 9px 5px 0!important;
	text-align: center!important;
	text-decoration: none!important;
	vertical-align: middle!important;
	background-position: center bottom!important;
	/*margin:3px 5px!important;*/
	width: 118px!important;
	-moz-border-bottom-colors: #363B42 #7D8692;
	-moz-border-left-colors: #363B42 #7D8692;
	-moz-border-right-colors: #363B42 #7D8692;
	-moz-border-top-colors: #363B42 #7D8692;
	-moz-border-radius: 4px!important;
	border-radius: 4px!important;
	border-radius: 4px!important;

background-image: -moz-linear-gradient(bottom, rgb(79,95,125) 10%, rgb(69,85,117) 57%)!important;
background-image: -moz-linear-gradient(bottom, rgb(50,54,59) 10%, rgb(105,108,112) 57%)!important;
}


input[type="text"],input[type="password"],textarea,select {
	-moz-appearance: none!important;
	-moz-border-radius: 3px!important;	
	border-radius: 3px!important;
	background: #2A303A none repeat scroll 0 0!important;
	border: 1px solid #77736B !important;
	color: #CCC!important;
	/*margin:0 3px 0 0!important;*/
	padding: 2px!important
}

select {
	-moz-appearance: none!important;
	height: 25px!important;
	padding: 0 9px 5px 0!important;
	vertical-align: middle!important
}

input[type="submit"]::-moz-focus-inner,input[type="button"]::-moz-focus-inner { border: 0 }

div#detailstitle input[type="text"] { width: 97% !important }

/*---- special RTM buttons ----*/
div.roundbtn_content, div.gobtn_content {
	width: auto!important;
	height: auto!important;
	padding: 2px 10px 5px!important;
	margin: 0!important
}

.xtd_tag {
	font-family: "Segoe UI" ,Tahoma,Verdana,Arial!important
}

a,.xtd_tag { color: #5378BF!important }

a:hover { color: #ddd!important }

#personName { color: orange!important }

body,.autocomplete_list,#iw_inner div { background: #030303!important; color: #fff!important }
#iw_inner div{background-color:rgba(3,3,3,0.4) !important}
.autocomplete_list {border:1px solid #272727 !important}
/*#appheaderlogo {background: url("/img/logo2.png") !important}*/
#pageheader { color: #9F9F9F!important; padding: 0!important }

#content {
	width: 98%!important;
	overflow: hidden!important;
	clear: both!important;
	padding: 5px 0!important
}

#mapview,#overview,#appview{
	width: 100%!important;
	float: left!important;
	position: relative!important;
	right: 22%!important
}
#mapbox,#locationsbox,
#overviewleft,#overviewright,
#listbox,#detailsbox
{
	float: left!important;
	position: relative!important;
	overflow: hidden!important;
	padding: 0 0 1em!important
}

#mapbox,#overviewleft,#listbox { width: 78%!important; left: 22%!important }

#overviewright,#detailsbox,#locationsbox { width: 20%!important; left: 23%!important }

#mapwrap,div.appfooter { float: left!important; width: 98%!important }

#overviewnews { padding: 0 .2em .2em 0!important }

#overviewcontinue,#overviewnewsbox,#overviewwhatsnew,.taskcloud {
	width: auto!important;
	background: #222!important;
	border: 1px solid #5378BF!important;
	margin-bottom: 5px!important;
	-moz-border-radius: 5px!important
	border-radius: 5px!important;
}

#overviewcontinue,#overviewwhatsnew,.taskcloud { border: 1px solid #272727!important }

div#detailswrap > div:first-child {
	border: 1px solid #272727!important;
	border-bottom: none!important;
	-moz-border-radius-topleft: 5px!important;
	-moz-border-radius-topright: 5px!important
	border-top-left-radius: 5px !important;
	border-top-right-radius: 5px !important;
}

#detailsoverflow {
	border: 1px solid #272727!important;
	margin-bottom: 5px!important;
	-moz-border-radius-bottomleft: 5px!important;
	-moz-border-radius-bottomright: 5px!important;
	border-top: none!important
	border-bottom-left-radius: 5px !important;
	border-bottom-right-radius: 5px !important;
}

#detailsstatuswrap,#keywrap,#onlinehelpwrap,#locations_display,#loc_key {
	border: 1px solid #272727!important;
	-moz-border-radius: 5px!important;
	border-radius: 5px!important;
	
}

/*---- task table ---- */
.xtable {
	color: #fff!important;
	border: 1px solid #272727!important
}

.xtd_prio { border-right: 1px solid #272727!important }

.xtd {
	border-bottom: 1px solid #272727!important;
	height: 32px
}

.xtr_select { background-color: #20282F!important }

td.xtd span.xtd_task_name {
	background: #030303!important;
	color: #fff!important;
	-moz-border-radius: 5px!important;
	border-radius: 5px!important;
	padding: 5px!important
}

.xtd_check form div input[type="checkbox"] { margin-right: -5px!important }

/*------- login ---------*/
div.contentboxwrap {
	border: 1px solid #5378BF!important;
	-moz-border-radius: 5px!important;
	border-radius: 5px!important;
}

#whatsnewbox,.taskcloudbox,#details,#list,#map { width: 100%!important }

/*---- tabs ----*/
#listtabs,#contacttabs,#settingstabs { border-bottom: 1px solid #272727!important }

.xtabs a,.xtabs li {
	background: none!important;
	border-bottom: none!important
}

.xtabs a {
	background: none repeat scroll 0 0 #0E0E0F!important;
	border: 1px solid #272727!important;
	border-bottom: none!important;
	-moz-border-radius-topleft: 5px!important;
	-moz-border-radius-topright: 5px!important;
	border-top-left-radius: 5px !important;
	border-top-right-radius: 5px !important;
	background: none repeat scroll 0 0 #0E0E0F!important;
	padding-left: 10px!important
}

.xtabs li.xtab_selected a {
	background-color: #3E5C8F!important;
	color: #000!important;
	text-shadow: #fff 0 0 15px!important;
	border: 1px solid #2B67AF!important
}

span#weeklyplanner {
	margin: 0!important;
	padding: 0!important;
	text-decoration: none
}

#weeklyplanner a {
	font-weight: 700;
	outline: medium none;
	text-decoration: none!important;
	padding: 0!important;
	margin: 0!important;
	border: none!important
}

a#overview-wp-link { font-size: .9em!important }

.snake-line,.snake-left-link,.snake-right-link { background: none #272727!important }

.new {
	background-color: #7DDFAF!important;
	color: #000!important;
	-moz-border-radius: 3px!important;
	border-radius: 3px!important;
	padding: 0 4px!important
}

/*---- sort list ----*/
#sorting { float: left!important; margin-top: 0!important }

.sortlist {
	left: 300px!important;
	/*margin-left:400px !important;*/
	margin-top: -126px!important;
	background: #000!important
}

#sort-list { border: 1px solid red!important }

/*---- status message ----*/
.orange_rbroundbox {
	background: none #578AAF!important;
	color: #000!important;
	-moz-border-radius: 5px!important;
	border-radius: 5px!important;
	border: 1px solid #2B67AF!important;
	padding: 8px!important
}

span#statusboxtext span a { color: #27394F!important }

.orange_rbcontent span { color: #000!important }

form[name="notificationform"] div,form[name="notificationform"] table {
	float: left!important;
	clear: both!important;
	width: 50%!important
}

/*---- task details ----*/
span#detailstitle_highlight,span#detailstitle_span { }

span#detailstitle_span { margin-left: 18px!important }

span#detailstitle_highlight a {
	float: left!important;
	position: absolute!important;
	width: 16px!important;
	height: 16px!important;
	margin-right: 2px!important;
	display: block!important;
	top: 55px!important
}

span#detailsdue_highlight > a,
span#detailsreoccur_highlight > a,
span#detailsduration_highlight > a,
span#detailstags_highlight > a,
span#detailslocation_highlight > a,
span#detailsurl_highlight > a {
	margin-right: 2px!important;
	width: 16px!important;
	height: 16px!important;
	float: left!important
}

span#detailstitle_highlight:hover,
span#detailsdue_highlight:hover,
span#detailsreoccur_highlight:hover,
span#detailsduration_highlight:hover,
span#detailstags_highlight:hover,
span#detailslocation_highlight:hover,
span#detailsurl_highlight:hover {
	background: none #578AAF!important;
	color: #000!important;
	-moz-border-radius: 2px!important;
	border-radius: 2px!important;
	border: 1px solid #2B67AF!important
}

span#detailstitle_highlight:hover { border: none!important; padding: 3px!important }
#locationsmanage,#datetime {font-size:0.9em !important}
#searchtoggle {font-size:1.1em !important}

#overview div,#appview div,#topbar,#footer,.white_rbroundbox,.white_rbcontent,.white_rbcontentwrap,.xtabs,.xtable,.add-box .ab2,.add-box .ab3,.add-box .ab4,.add-box .ab5,.add-box .ab7,.add-box .ab9, .sortbtn td.lft,.sortbtn td.mid,.sortbtn td.rgt,.xtd_ico.has_note { background: none!important }

/*---- background image ----*/
#pageheader {
	background: #1b1b1b url(data:image/png;base64,)!important
}

/*---- header logo ---*/
#appheaderlogo {
	background: url(data:image/png;base64,)!important
}

#searchicon {
	background: url(data:image/png;base64,) no-repeat !important
}

#onlinehelpcontent img[src*="ico_help_blu.gif"],
img[src*="ico_pro.gif"],
#detailsstatuscontent img,
#printlist img,
#exportlist img,
#exportlistevents img,
#atomlist img,
#permalist img,
b[class*="blue_rb"],
.orange_rbtop,.orange_rbbot,
span#detailstitle_highlight a img,
span#detailsdue_highlight a img,
span#detailsreoccur_highlight a img,
span#detailsduration_highlight a img,
span#detailstags_highlight a img,
span#detailslocation_highlight a img,
span#detailsurl_highlight a img,
b.roundbtnwrap, b.gobtnwrap,
.white_rbtop,.white_rbbot { display: none!important }

#printlista,#icalendarlista,#icalendareventslista,#atomlista,#permalista { padding-left: 18px !important }

#printlista  {
	background: url(data:image/png;base64,) no-repeat 0 0 !important;
	padding-left: 18px !important
}

#icalendarlista, #icalendareventslista {
	background: url(data:image/png;base64,) no-repeat !important
}

#atomlista {
	background: url(data:image/png;base64,) no-repeat !important
}

#permalista {
	background: url(data:image/png;base64,) no-repeat !important
}

/*--- move/reset images ----*/
#add-helpicon, .smartadd-keyicon, img[alt="Information"], .noteaddicon, .detailsaddicon, .locationaddicon, .iw_close, #loc_key_close, #loc_key_icon, div.locsearch img.xtarget,#searchadvtip img  {
	padding-left: 17px !important;
	padding-right: 0 !important;
	height: 16px !important;
	width: 0 !important
}
/*---- cross icon ----*/
img.iw_close, #loc_key_close {
background: url(data:image/png;base64,) no-repeat !important
}
#loc_key_close {margin:2px 2px 0 0 !important}
/*---- plus icon ----*/
.noteaddicon, .detailsaddicon, .locationaddicon {
	background: url(data:image/png;base64,) no-repeat !important
}
/*---- key icon ----*/
#loc_key_icon
{
background:url(data:image/png;base64,) no-repeat !important
}
/*---- small search icon ----*/
div.locsearch img.xtarget
{
background:url(data:image/png;base64,) no-repeat !important;
	padding-left: 14px !important;
margin-top:1px !important
}

#add-helpicon, .smartadd-keyicon {
background: url(data:image/png;base64,) no-repeat !important
}

/*---- question mark ----*/
#onlinehelpcontent span#onlinehelptext,#searchadvtip img {
	background: url(data:image/png;base64,) no-repeat !important;
	padding: 0 0 0 20px!important
}

/*---- note icon ----*/
.xtd_ico.has_note, td.has_note {
	background: url(data:image/png;base64,) no-repeat 0 8px !important
}

span.ico a[href="/upgrade/"] {
	background: url(data:image/gif;base64,)!important;
	width: 20px!important;
	height: 12px!important;
	display: block!important;
	float: left!important
}

#upgrade ul.feat li {
	background: url(data:image/gif;base64,) no-repeat!important
}

img[alt="Information"],
span#detailsstatustext {
	background: url(data:image/png;base64,) no-repeat!important;
	padding-left: 20px!important
}

span#detailstitle_highlight > a {
	background: url(data:image/png;base64,) no-repeat!important
}

span#detailsdue_highlight > a {
	background: url(data:image/png;base64,) no-repeat!important;
	width: 16px!important;
	height: 16px!important;
	float: left!important
}

span#detailsreoccur_highlight > a {
	background: url(data:image/png;base64,) no-repeat!important
}

span#detailsduration_highlight > a {
	background: url(data:image/png;base64,) no-repeat !important
}

span#detailstags_highlight > a {
	background: url(data:image/png;base64,) no-repeat !important
}

span#detailslocation_highlight > a {
	background: url(data:image/png;base64,) no-repeat !important
}

span#detailsurl_highlight > a {
	background: url(data:image/png;base64,) no-repeat !important
}

#detailswrap, div#overviewtabs + div, #searchboxwrap table,div#listwrap div { border: none!important }

.xtr_hover, .xtr_hover td.xtd span.xtd_task_name,.xtr_select td.xtd span.xtd_task_name { background-color: #1e262e!important }

#footer-news span.new, td.icons span { display: block!important; float: left!important }

span,.keyprio, .xtd_text, #keytitle,.field { color: #fff!important }

div.roundbtn, div.gobtn_content, div#detailsbox div, #overviewcontinue,#details,#detailswrap,#keywrap,#detailsstatuswrap, div#detailsbox div,#locations_display,#loc_key { width: auto!important }
}
