css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newegg.com") {

/* Hide irritating "Google Trusted Store" badge */
div#gts-c {
  display: none;
}

/* Remove brand images from product listings */
.listStyleBrand {
  display: none;
}

/* Show the hidden-in-list-view-but-more-useful set of features */
.productList .itemFeatures {
  display: block !important;
  font-size: 1em !important;
}

/* Hide the longer, less useful feature list */
.productList .featureList {
  display: none !important;
}

/* Remove banner ads, entire right column, etc. (waste space / distract, never relevant/interesting) */
#bannerAdTop, .bodyRightArea, .footerAwards {
  display: none !important;
}

/* Clean up front page */
.promoSections, .mktpSpecials, #personalizationWrapper, .secondaryNav, .bnrPromo, .bnrPromo_full, .brandBnr, .hero, .shoppingGuides, #shoppingTools, .emailList, .testimonial, .bnr_left, .grpSocial {
  display: none !important;
}

}
