css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("apps.rackspace.com") {

#wm_core { padding: 0 0 !important ; margin: 0 5px !important; }

#wm_core_header, #wm_core_header table { min-height: 0 !important; height: 100% !important; }

#wm_core_navlinks { float: left !important; padding-top: 0.6em !important; margin: 0 !important; }

#wm_core_search { float: right !important; padding: 0.2em 0 !important; margin: 0 !important; }

#wm_core_toolbar { padding: 1px 0 !important; }

/* Sidebar: Header smaller, folders more spaced out */
.Widgets_Controls_SidebarSection { margin: 6px !important; }
.Widgets_Controls_SidebarSection .Email_SubMenu li { font-size: 1.1em !important; line-height: 2em !important; }
.Widgets_Controls_SidebarSection .header { min-width: 0px !important; }
.Widgets_Controls_SidebarSection .header img { margin: 0px 1px !important; }
.Widgets_Controls_SidebarSection .header .title { font-size: 9px !important; }

.wm_logo { display: none; }

/* fix an error in the search button */
.Widgets_Toolbar li { padding: 0 0px 0 4px !important; }

/* scrunch contents of email header */
td.val, td.lbl { font-size: 0.9em !important; line-height: 1.5em !important; }
.subject { font-size: 1em !important; line-height: 1.5em !important; font-weight: bold !important ; text-decoration: underline !important; }

}
