css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.metafilter.com/contribute/activity/") {

/* Scrunch comments */
.copy div div {
  font-size: 0.5em !important;
  line-height: 1.4em;
  max-height: 100px;
  overflow: auto;
  border-bottom: 1px dotted #aad;
  padding: 4px;
}

/* Hackishly fix some other stuff */
.copy div :first-child {
  font-size: 0.9em !important;
}
.copy :first-child :first-child {
  font-size: 1.1em !important;
}
.copy div div .smallcopy {
  font-size: 1.5em !important;
  line-height: 1.5em;
}

}
