css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("minichan.org") {
 body{
  background:#000 url(http://cdn.store.steampowered.com/public/images/v5/content_bg.png) repeat-x !important;
  color:#d8ded3 !important;
  padding:0 !important;
  }
  .body{
   background-color:#3a3a3a !important;
   margin:0 1em 0.5em 1em !important;
   padding:.3em 0 0 .5em !important;
  }

 span.quote{
  color:#fff !important;
 }

 .highlighted{
  background-color:#63605e !important;
 }

 .spoiler{
  background-color:#d8ded3 !important;
  color:transparent !important;
 }
  .spoiler:hover{
   color:#314159 !important;
  }

 .border {
  background-color:#314159 !important;
  border:3px dashed #000 !important;
  margin:1em;
  padding:0.5em;
 }

 .highlight {
  background-color:#000 !important;
  font-style:normal;
  font-weight:700;
 }

 a{
  color:#d8ded3 !important;
 }

 #notice{
  background-color:#63605e !important;
  padding:.2em !important;
 }

 h1{
  display:none !important;
 }

 h2{
  font-size:18px !important;
  margin:2.3em 0 .3em 1em !important;
 }

 h3{
  background-color:#63605e !important;
  font-size:14px !important;
  margin:0 0 0.5em !important;
 }

 h4.section{
  margin:0 1em 1em !important;
 }

 textarea{
  margin:0 0 1em !important;
  padding:2px !important; 
 }

 input[type="text"]:focus,input[type="password"]:focus,textarea:focus{
  background-color:#fff6d1 !important;
  outline:1px solid #e8c177 !important;
 }

 fieldset{
  margin:1em !important;
 }

 ul.menu{
  padding:0 !important;
 }

 ul#main_menu{
  background-color:#63605e !important;
  margin:0 !important;
  padding:.8em !important;
  position:fixed !important;
  top:0 !important;
  left:0 !important;
  width:100% !important;
 }

 table{
  margin:0 1em 1em 1em !important;
 }
  table th{
   background-color:#63605e !important;
  }
  table td{
   background-color:#1e1e1e !important;
  }
  table tr.odd td{
   background-color:#191919 !important;
  }
  table tr:hover td{
   background-color:#314159 !important;
  }

 em.marked{
  background-color:#63605e !important;
 }

 #last_seen_marker{
  border-top:2px solid #494949 !important;
 }

 #snapback_link{
  font-size:18px !important;
  font-weight:700 !important;
 }

 .standalone,.row{
  margin:0 1em 1em !important;
 }
}
