css:
@-moz-document domain("www.reddit.com") {
.sidebox.create {display: none !important;}
.sidebox.submit {display: none !important;}
.sidebox.gold {display: none !important;}
.organic-listing {display:none !important;}
#sr-header-area {display: none !important;}
.promoted.link {display: none !important;}
.footer.rounded {display: none  !important;}
#header {border-bottom: 10px solid #CEE3F8 !important;}
.tabmenu li.selected a {border: 0px solid  !important;}
.tabmenu li a {padding: 6px 12px 6px 12px  !important;}
.tabmenu li {margin-left:5px !important; margin-right:5px !important; font-weight: normal !important;}
.tabmenu {margin-bottom: 0px !important; vertical-align: top !important; margin-top: 16px !important;}
#header-bottom-right {border-top-left-radius: 0px 0px !important; padding: 6px !important; margin-bottom: 2px !important; margin-right: 10px !important; font-size: 12px !important; vertical-align: top !important; display: inline-block !important;}
.content .spacer {margin-bottom: 10px !important;}
.menuarea {border-bottom: 0px !important;}
.side .spacer {margin: 0px 0px 12px !important;}
.side {margin: 0px 14px !important;}
#search input[type="text"] {border: 0px !important; background-color: #EFF7FF !important; color: #369 !important;}
#search input[type="text"] {padding: 5px !important; font-size: 16px !important; margin-top: 10px !important; margin-right: 10px !important;}
#header-img {background: url("http://i.imgur.com/k3YIp.png") no-repeat !important; width: 78px !important; padding: 26px 0 0 0 !important; height: 0 !important; overflow: hidden !important; margin-top: 10px !important; margin-left: 14px !important; margin-right: 6px !important;}
.pagename {font-variant: normal !important; font-weight: bold !important; background-color: #CEE3F8 !important; padding: 6px 6px 6px !important; margin-right: 6px !important; vertical-align: top !important; margin-top: 10px !important; display: inline-block !important;}
.pagename a {color: #369 !important;}
.titlebox {background-color: #EFF7FF !important; padding: 6px 10px !important;}
.sidecontentbox {background-color: #EFF7FF !important; padding: 6px 10px !important;}
#ad-frame {display:none !important;}
.infobar {display:none !important;}
.login-form-side {background-color: #EFF7FF !important; border: 0px !important; padding: 6px 10px !important;}
.login-form-side input {width: 126px !important;}

}
