css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("chrome://fastdial/content/fastdial.html") {
/**
* @title Opera Speed Dial
*/
body {
	background: -moz-linear-gradient(100% 100% 90deg,#DCDCDC, #FDFDFD) ;
	font-family: "Tahoma" ;
	font-size: 11px ;
	height:100% ;
	padding:0 ;
}

#layout {
	height: 100% ;
}

.back {
	background-image: url(chrome://fastdial/skin/images/back.png) ;
}

.search-input {
	background-image: none ;
	color: #000000 ;
	background-color: #fff ;
	border: 1px solid transparent ;
}

.thumbnail {
	background: #fff ;
	border: 1px solid #949494 ;
	-moz-border-radius:5px ;
	-moz-box-shadow: 0px 0px 10px -2px #535353 ;
	margin: 8px ;
	opacity: .9 ;
}

div.thumbnail:hover, .hover {
	border-color: #535353 ;
	margin: 8px ;
	opacity: 1 ;
}

.body {
	margin:4px ;
}
	
	.image {
		outline:3px solid white ;
		outline-offset:-1px ;
		-moz-outline-radius:7px ;
	}

.title {
	visibility: visible ;
	color: #444 ;
	background: transparent;
	border:0 ;
	bottom:3px ;
	font-family: Tahoma ;
	font-size:10pt ;
	
}
	.title img {
		display: inline ;
	}

div.thumbnail:hover .title, .hover .title {
	visibility: visible ;
	color: #000 ;
}

div.thumbnail:hover .title img, .hover .title img {
	display: inline ;
}

}
