css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pivotaltracker.com") {
 #header {
  background:white !important;
}
img[alt="PivotalTracker"]{
visibility:hidden;
}


#projects_button{
position:absolute;
left:6px;
background:white;
margin: 0 20px 4px 0 !important;
}

  .layout {
     border-spacing: 4px 0 !important;
  }
  a[alt="PivotalTracker"] {
    display:none;
 }

#header #projects_button {
margin:0 !important;
}

.welcome_message {
display:none;
}

.spacer {
width: 5px !important;
}

#project_name_row a{
  margin: 4px 4px !important;
  color: white;
}

#controls_search_velocity_row{
position: absolute;
top: 0px;
margin-top:4px;
left: 100px;
width: 1168px
}

#buttonPanel {
position:absolute;
top: 4px;
background: white;
padding: 0 0 3px 10px !important;

}

.searchField {
margin-top:4px !important;
background: white;
height: 30px !important;
}

#velocity {
background: white;
margin-right:10px;
}

.simple_footer{
display:none
}

#controlPanel #velocity {
padding-right: 0px !important;
}

.navigation_area{
position: absolute !important;
bottom:6px !important;
left: 350px !important;
font-size: 80% !important;
line-height:100% !important;
}

.underEdit .storyDetails {
background-color:white;
padding-bottom:6px;
padding-left:8px !important;
padding-right:10px !important;
}

.storyPreviewText, .storyPreviewInput {
padding-left:10px !important;
}

#controlPanel  {
margin-bottom:0px !important;
margin-left:0px !important;
min-width:930px;
padding-bottom:0px !important;
padding-left:10px;
padding-top:0px !important;
}

#project_name_row{
   display:none;
   position:absolute;
   top:0px;
   left:108px;
   width: 400px;
   height:30px;
   background: #88C2E5;
   padding-top: 0px !important;
   font-family: Georgia;
   font-style:italic;
}

}
