css:
@-moz-document url("chrome://speeddial/content/speeddial.xul"){
/* Created by treasury - google.com/+ViktorKincses */
.sd-tabpanel,.sd-tab[selected="true"],window, page, dialog, wizard, prefwindow {
background-color: rgb(70, 70, 70) !important;
color: rgb(221, 221, 221) !important;
}

.sd-tabpanel{
padding:3px 3px !important
}

.sd-tabs-left, .sd-tabs-right {
-moz-border-bottom-colors: #aaa !important;
border-bottom: #aaa 1px solid !important;
}

.speeddialThumb {
margin:6px !important;
border:0px #fff solid;
-moz-border-radius: 6px !important;
border-radius:6px !important;
padding:5px;
background:#999 url("http://i25.tinypic.com/w7hmb5.png");
-moz-box-shadow: 1px 2px 4px #000;
box-shadow:  1px 2px 4px rgba(0, 0, 0, 0.8);
/* Created by treasury - google.com/+ViktorKincses */
}

.speeddialThumb:hover{
background:#ddeeff !important;
}

.speeddial-top-left,.speeddial-top-middle,.speeddial-top-right,
.speeddial-middle-left,.speeddial-middle-middle,.speeddial-middle-right,
.speeddial-bottom-left,.speeddial-bottom-middle,.speeddial-bottom-right{
display:none;
}

.speeddial-container{
margin:0 !important;
border:0 !important;
}

}
