css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("craigslist.org"), domain("craigslist.at"), domain("craigslist.ca"), domain("craigslist.ch"), domain("craigslist.cz"), domain("craigslist.es"), domain("craigslist.fi"), domain("craigslist.gr"), domain("craigslist.it"), domain("craigslist.jp"), domain("craigslist.pl"), domain("craigslist.pt"), domain("craigslist.se"), domain("craigslist.co.in"), domain("craigslist.co.kr"), domain("craigslist.co.th"), domain("craigslist.co.uk"), domain("craigslist.co.za"), domain("craigslist.com.au"), domain("craigslist.com.cn"), domain("craigslist.com.mx"), domain("craigslist.com.ph"), domain("craigslist.com.sg"), domain("craigslist.com.tr"), domain("craigslist.com.tw") {

body {background: url("http://imgur.com/Zyjsy.jpg") #222222 fixed repeat left top!important; color:#ffffff !important; text-shadow: #000000 1px 1px 2px, #000000 -1px -1px 2px !important;font-family: arial, calibri !important;}

*{margin-left: auto !important; margin-right: auto !important;}

a {color: #cccccc !important; text-decoration: none !important;}
a:visited {color: #888888 !important;}

h4 a, h4, h5, .ban a, .p, #logo span, .www td b, #update, #sidebar h2, .post h2 a, pre, .sh {color: #6699ff !important;}

#logo a, sup a, #postlks a, #leftlinks a, #alks a, .bchead a, blockquote table a, .posting a, font[color="purple"], .post a, #userbody a {color: #99ccff !important;}

.post small{color: #99ffcc !important;}



a:hover {color: #99ccff !important; text-shadow: #ffffff 0px 0px 2px !important; text-decoration: none !important; }



em {color: #ff6666 !important;}

.greytext, p {color: #ffffff !important;}

input, select, tr[bgcolor="#eeeeee"] {-moz-appearance: none; background: rgba(0,0,0,.5) !important; color: #999999 !important; border: none !important; padding: 3px !important;}
table[style="white-space: nowrap; background: none repeat scroll 0% 0% rgb(238, 238, 238); border: 1px solid gray;"] {border: none !important;}

#leftbar, #search, .city, #main, #topban, #topban > div, li, .cal td, .cal, #alks, #alks td, blockquote p, blockquote, .www td, .post, #footer, tr[bgcolor="#eeeeee"], fieldset, .posting h2 {background: rgba(20,20,20,.2) !important; }

table[summary="page"], table[style="border: 1px solid gray; background: rgb(238, 238, 238) none repeat scroll 0% 0%; white-space: nowrap; -moz-background-clip: border; -moz-background-origin: padding; -moz-background-inline-policy: continuous;"], #tsb, #userbody, #sc, .www, span[style="background-color: rgb(255, 255, 153);"], #page, #header, blockquote table, #pp blockquote {background: rgba(20,20,20,.4) !important;}

#logo, #search div, #banner, a, #pp li {background: transparent !important;}

blockquote p:hover, #postlks li, h5, h4, h3, .ban, .dys th, #leftlinks li, .bchead, #flags, .www td b, .wwl td a, .post h2, #prinfo, #shareSocial, tr[bgcolor="#dddddd"], td[bgcolor="#dddddd"], .highlight, .sh{background: url("http://lh3.google.com/preston.scheuneman/R1m-ja98gjI/AAAAAAAAADI/KGg9nz8KuPI/GlassShiny.png") fixed repeat left center !important; border-color: #000000 !important; }


li, table[style="border: 1px solid gray; background: rgb(238, 238, 238) none repeat scroll 0% 0%; white-space: nowrap; -moz-background-clip: border; -moz-background-origin: padding; -moz-background-inline-policy: continuous;"], blockquote {border: transparent !important; color: #aaaaaa !important;}


#main {width: 600px !important; margin-left: 10px !important; margin-right: 18px !important;}


#ccc1, #ccc2, #sss1, #sss2, #bbb1, #bbb2, #ggg1, #ggg2  {width: 50% !important;}
#forums1, #forums2, #forums3 {width: 33.33334% !important;}
#alks td {text-align: center !important; width: 50% !important;}
body:not(.hp) {width: 800px !important;}
.toc {width: 800px !important;}
.posting {width: 1000px !important;}
#sc #banner {height: 25px !important;}

#pp ul {padding-bottom: 20px !important;}
.city li {padding: 1px !important;}
blockquote p {padding-left: 15px !important; padding-top: 5px !important; padding-bottom: 5px !important; margin-top: -10px !important; padding-right:15px !important;}
.entry blockquote {padding-left: 0px !important;}
.sh {margin-bottom: 8px !important;}
#userbody {padding: 5px !important;}
#content h2 {margin-left: 170px !important;}
#sc {margin-left: -170px !important; }
#update {position: absolute !important; top: 0px !important; margin-left: 500px !important; width: 150px !important;}
#sidebar {margin-left: 550px !important;}
#prinfo {position: absolute !important; margin-left: 520px !important;}



#content .post h2 {margin-left: 0px !important;}

#list, blockquote blockquote blockquote h4 , blockquote > h3, #leftbar {padding-left: 15px !important;padding-right: 15px !important;}
#postlks li, #leftlinks li, .wwl td a{-moz-box-shadow: 0px 0px 3px 0px #000000, inset 0px 0px 3px 0px #000000;}
#list {padding-bottom: 15px !important;}

#postlks li:hover, #leftlinks li:hover, .wwl td a:hover{-moz-box-shadow: 0px 0px 1em 0px #FFFFFF, inset 0px 0px 3px 0px #FFFFFF;}

h4, h5 { -moz-box-shadow: 0px 0px 3em #000000; }

#main tbody, .city, #leftbar, blockquote, .bchead, #userbody, .www, #page{-moz-box-shadow: 0px 0px 2em 1px #000000;}

.toc, .toc td, #sc, .greytext {font-size: .75em !important;}
.toc h4 {font-size: 1.2em !important;}

input[type="submit"] {font-size: .9em !important;}
font[size="+3"] { font-size: 2.0em !important; color: #ff6666;}

#userbody img:hover{-moz-box-shadow: 0px 0px 3em 3px #99ccff !important;}
}
