css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("timetask.com") {
body { font-family: Helvetica, FreeSans, Arial, sans-serif !important;
background-color: #eee !important; }
#allLeft, #allRight { background: transparent !important; }
#allMain { -webkit-box-shadow: 0px 0px 15px #aaa;
-moz-box-shadow: 0px 0px 15px #aaa; padding-bottom: 10px !important; background: white !important; margin-bottom: 15px !important; }
a, a:visited { color: black !important; text-decoration: none !important; }
a:hover { text-decoration: underline !important; }
div#navCell a,
div#navCell li, div#navCell a:hover span, div#navCell li.navTabOn a span { background-color: transparent !important; }
div#navCell a:hover span, div#navCell li.navTabOn a span { color: white !important; }
div#navCell a:hover, div#navCell li.navTabOn a, div.pinstripe {
background-color: #333 !important; color: black !important; text-decoration: none !important;
}
div#navCell li#navListOptions { margin-right: 0px !important; }
div#navCell a span { position: relative; top: 2px; }
div#subnav { background: #eee !important; border-bottom: 1px solid #333 !important; padding: 8px 10px !important; color: #aaa !important; }
#subnav input.submit { margin-top: 6px !important; padding: 1px 7px !important; }
div#subnav ul li.subnavOn { padding: 3px 5px !important; background: #ddd !important; border: 1px solid #ccc !important; -moz-border-radius: 10px !important; -webkit-border-radius: 10px !important;  }
div#navCell li.nlcSub span { background-color: transparent !important; }

div#navOptions li.separator { border-bottom: 1px solid #ddd !important; }
div#navOptions ul { border: 1px solid #ddd !important; }
div#navOptions a:hover { background: #ddd !important; }

div#globalIconCell, div#brandCell, div#allFooter { display: none; }
div#navCell ul { padding: 10px 20px 0 !important; }

.iconsAlert, .iconsHialert { background: transparent !important; padding-left: 0 !important; }

div#alertCell {
border:2px solid #DDDDDD !important;
float:none !important;
margin:5px 20px !important;
overflow:hidden !important;
padding:5px !important;
}
div#alertCell ul li { margin: 0 !important; }
div#body {
margin:25px 20px 10px !important;
}

#task_list_taskcount { display: none !important; }
div#navCell li { margin-right: 10px !important; }
div#navCell a span { font-size: 14px !important; }

input.submit { background-color: #888 !important; color: white !important; border-color: #777 !important; -moz-border-radius: 10px !important; -webkit-border-radius: 10px !important; }
input.submit:hover { background-color: #666 !important; border-color: #555 !important; }
input.submit:focus, input.submit:active { background-color: black !important; border-color: black !important; }

div#supportFilter, div#supportFilterSub, div#supportFilterButton { background-color: white !important; margin-top: 0 !important; }
div#all { padding-bottom: 15px !important; } 
div#dock td#dockProfile { display: none !important; }
#dockSearch > a { display: none; }
div#dock td#dockSearch { padding-top: 12px !important; }
div#dock table.dock { -webkit-box-shadow: 0px 0px 15px #aaa;
-moz-box-shadow: 0px 0px 15px #aaa; border: none !important; }
table.newTable { width: 100% !important; }
table.newTable th { text-align: center !important; background-color: #eee !important; border: 1px solid #aaa !important; }
table.dashboardTable { background: white !important;  }
table.newTable td { border: 1px solid #aaa !important; }
#home_index_table { margin: 20px 0 !important; }
#dashboardBadgeBg { display: none !important; }
table.newTable .sortASC, table.newTable .sortASC_narrow, table.newTable .sortASC_right, table.newTable .sortDESC, table.newTable .sortDESC_narrow, table.newTable .sortDESC_right { background-color: #CCC !important; font-weight: bold !important; }
table.newTable tr.even, table.newTable tr.odd { background-color: white !important; }
table.newTable .context { font-weight: bold !important; }
table.newTable tr:hover { background-color: #ccc !important; }
#taskUtilityPreview, #urlForm,
h3#pNotes { display: none !important; }
#task_tabs ul li.on, #task_tabs ul li.on a { background-color: #333 !important; color: white !important; }
#thistory thead { display: none !important; }
div#task_tabs ul li, div#queue_tabs ul li, div#invoice_tabs ul li, div#report_tabs ul li { padding:10px 11px 8px !important; }
div#task_tabs ul li, div#queue_tabs ul li, div#invoice_tabs ul li, div#report_tabs ul li { background-color: white !important; position: relative !important; left: -1px !important; }
div#task_boxes, div#queue_boxes, div#invoice_boxes { border: 1px solid #333 !important; }
.taskViewChunk tr, .taskViewChunk th { background-color: white !important;  }
#note_body tr.odd, #note_body tr.even { background-color: white !important; border: 1px solid #aaa !important; }
#note_body tr.odd:hover, #note_body tr.even:hover { background-color: #ddd !important; }
.noteLeft img { display: none !important; }
h2#tasks-view-title-heading { position: relative !important; top: 20px !important; display: inline !important; }
table.fixed td#supportCell, a.tooltip { display: none !important; }
table.fixed, table.stretch  { width: 100% !important; }
div#utility { margin-right: 0px !important; }
}
