css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dpreview.com") {

body, .sideBar, .chromeMainBottom, .chromeMainTop, .contentWrapper, div.chromeMainMiddle, .forumslinkleft, .tdlabelsimpsm, .forumslinksmall {
background: #ccc !important;
color:#000;
}

div.mainContent {
  padding-left:0 !important;
}

.siteHeader {
  width:902px !important;
}


.contentTable, .widget,#mainContent{
background-color: #cbe3fe !important;
color:#000;
padding:1em !important;
}

#mainContent td {
background-color: #cbe3fe !important;
color:#000;
}

.sideBar{
}

.widget a, div.mainContent a {
  color:#00f !important;;
}

div.mainContent .forumscellleft a, div.mainContent .forumscell a, div.mainContent .userHover, div.mainContent .articleFooter a, table .table-std6px a, #content_tabs a {
  color:#ff0 !important;
}

.widget .header, .widget .header a , .scoring, .h4size, .table-std6px-sm, .table-std6px, .table-std, .tdlabelsm, .tdcontentsm, .small11, .articleFooter, #content_tabs {
color:#fff !important;
}

.entry .entry-subheader {
  color:#000 !important;
}

table {
  border-collapse:collapse  !important;
}

div.mainContent .green {
  color:darkgreen !important;
}

#site-searchbox {
  margin-left:-5em !important;
}

.forumshead, .forumshead td, .forumscellright, .forumscellrightsel, .forumscellleftsel, .keyboardDarkBigBoxBtm, .forumscellc {
  color:#fff!important;
}

td.forumsmessagecell, td.forumsmessagelabel,td.forumsmessagemiddle,td.forumsmessagebottombar, td.forumsmessagefootersingle,td.forumsmessagecontainer,td.forumsmessageheader,td.forumsmessagefooter{
  background:#ccc  !important;
}

td.forumsmessagecontainer .small11 {
  color:red !important;
}

td.forumsmessagecontainer .small11 {
  color:darkgreen!important;
}

td.forumsmessagecontainer .small11 a, .forumsmessagecell a.userHover{
  color:blue!important;
}

td.forumsmessagecontainer .h4size {
  color:#666!important;
}

td.forumslinkleft, td.tdlabelsimpsm, td.forumsmessagelabel, td.forumsmessagecell, td.forumslinksmall{
  border:1px solid #aaa !important;
  empty-cells:hide;
  padding:0 !important;
}

.galleryAttachment p a, .galleryPhoto p a, .forumscellc a, #actionBar a, .ddltitle{
  color:#ffff00!important;
}

#mainContent table[width="590"] td.forumscellleft,
#mainContent table[width="590"] td.forumscell,
#mainContent table[width="590"] td.forumscellc,
#mainContent table[width="590"] td.forumscellright {
  background:#888!important;
  border-top:1px solid #ccc;
  border-collapse:collapse;
}

}
