css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://dabr.co.uk/") {


a{color:#004040!important;}
form{margin:0px!important;}
body{background:#F0F0F0!important;
color:#222!important;margin:7px!important;;font:13px tahoma!important;}

  table{border-collapse:collapse}
  td{vertical-align:top;padding:7px!important;}

tr.odd td{background:#F0F0F0!important;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
padding: 7px!important;
}
tr.even td{background:#F9F9F9!important;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
padding: 7px!important;
}

  tr.reply td{background:#FFFFCC!important;}
  tr.reply.even td{background: #FFFFCC!important;}

  .menu{
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
color:#F0F0F0!important;background:#F9F9F9!important;padding: 2px!important; margin-bottom: 7px;}
  .menu a{color:#222!important;text-decoration: none!important; margin: 1px!important;}
#menu a{ color: #222!important; }

body{
width: 600px;
float: right;
text-align:justify;direction:rtl;font-family:tahoma;font-size: 10pt !important;line-height: 20px!important;
}

}
