css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("facebook.com") {







#contentCol  { background-color:#F3FCFF !important; }
.profile .profile_color_bar  { background:#daf9e0 url('http://a.imageshack.us/img836/558/adfvaegvfsrgw4g.png') top center !important; }



#blueBar       { background:#78F0DD url('http://a.imageshack.us/img19/794/faefaegvsegesgseg.png') center bottom !important; }
#pageLogo a    { background:url(http://a.imageshack.us/img820/7994/10w0.png) no-repeat scroll -21px 0 !important;}
#headNavOut    { background:#B2F6E5 url('http://a.imageshack.us/img22/2933/zsdvsezgsrgsrg.png') top left !important; border-color: rgba(114,163,132,0.7) !important; }

	



.jewelToggler  { background-image:url(http://a.imageshack.us/img9/7994/10w0.png) !important; }
#jewelAlert:hover, #jewelAlert:focus, #jewelAlert:active,
#jewelMail:hover, #jewelMail:focus, #jewelMail:active,
#jewelRequest:hover, #jewelRequest:focus, #jewelRequest:active   { background-color:#B2F6E5 !important; }
#jewelCase .jewel  { border:none !important; }




#navSearch button  { background:url(http://a.imageshack.us/img9/7994/10w0.png) no-repeat scroll -1px 0 !important; }
#navSearch     { border-color:#78F0DD !important; }




#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#26DEB9 !important; color:#ffffff !important; }




.lightblue_box  { background-color:#E2FCF5 !important; border-color:#B2F6E5 !important; }
.commentable_item .ufi_section  { background-color:#E2FCF5 !important; }
textarea, .inputtext, .inputpassword  { border-color:#B2F6E5 !important; }




a  { color:#26DEB9 !important; }
.uiLinkButton input  { color:#2c2c2c !important; }
.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color:#2c2c2c !important; }
button.as_link  { color:#2c2c2c !important; }




.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#B2F6E5 !important; }

.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:#E2FCF5 !important; }
.friends_online_sidebar li.buddy_row a:hover  { background-color:#E2FCF5 !important; }

.unread  { background-color:#B2F6E5 !important; }
.line  { color:#26DEB9 !important; }




#jewelCase .jewelFooter a   { background-color:#E2FCF5 !important; }
#jewelCase .jewelFooter .uiTextSubtitle  { color:#26DEB9 !important;  }

#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color:#26DEB9 !important; }

#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#E2FCF5 !important; border-color:#78F0DD !important; }
#jewelCase .jewelBox  { background-color:#F3FFFC !important; }




.uiHeader h2  { color:#26DEB9 !important; }




.current_stuff td  { background-color:#F3FFFC !important; border-color:#B2F6E5 !important; }




.UIComposer .UIComposer_Box  { background-color:#F3FFFC !important; border-color:#B2F6E5 !important; }
.profile .box .box_header  { background-color:#F3FFFC !important; border-color:#B2F6E5 !important; }




.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ffffff !important; border-color:#B2F6E5 !important; }
.profile .top_bar ul.tabs li a.tab_link  { background-color:#B0F6E5 !important; border-color:#78F0DD !important; }
.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#26DEB9 !important; color:#ffffff !important; border-color:#26DEB9 !important; }





#chat_tab_bar .disabled .chat_window .chat_conv  { background-color:#F3FFFC !important;  }
#chat_tab_bar .chat_header  { background-color:#78F0DD !important; border-color:#000000 !important; }
#chat_tab_bar .chat_header_name a  { color:#ffffff !important; }
#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color:#B2F6E5 !important; color:#26DEB9 !important; }
#presence_bar .presence_menu_opts h2 span  { background:#78F0DD !important; }
#presence_bar .presence_menu_opts h2  { border-color:#78F0DD !important; }
.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background:#F3FFFC !important; }

#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#78F0DD !important; border-color:#78F0DD !important; }
#presence .presence_bar_button .inner_button  { border-top:none !important; }
#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ffffff !important; }

#presence .presence_menu_opts  { border-color:#78F0DD !important; }




.UIButton_Blue  { background:#26DEB9 !important; border-color:#78F0DD !important; }
.UIComposer_InputArea  { border-color:#B2F6E5 !important; }
.uiButtonConfirm  { background:#26DEB9 !important; border-color:#78F0DD !important; }
#presence #buddy_list_tab #buddy_count strong  { color:#ffffff !important; }














}
