@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spaces.ru"), domain("fank.ru")  {

body{
  font-weight:normal;
  font-size:medium;
  font-family: Verdana !important;
  background-color:hsl(260,100%,15%) !important;
  color: hsl(260,20%,50%) !important;
  max-width:800px !important;
  border:1px solid hsl(260,100%,20%) !important;
  /* background: url(images/Spaces.jpg) fixed  top left !important;  */ }

#reklama{ display: none !important;}

a:link {
  color: hsl(260,100%,30%) !important;
  font-weight:bold !important}
a:visited{
  color: hsl(1,0%,50%) !important;}
a:hover{
  color: hsl(91,100%,30%) !important;
  background-color: hsl(91,100%,15%) !important}
.newevent {
  color:hsl(1,70%,70%) !important;
  font-size:small;
}


div.row4{
  color: hsl(260,35%,20%) !important;
  background-color: hsl(260,100%,80%) !important}
div.odd, div.blueline,div.row3{
  color: hsl(260,35%,30%) !important;
  background-color: hsl(260,70%,80%) !important;
  border: hsl(260,70%,80%) !important }
div.even, div.main{
  color: hsl(260,35%,30%) !important;
  background-color: hsl(260,40%,80%) !important;
  border: hsl(260,70%,80%) !important }

#navi, #footer_path, #header_path{
  color: hsl(260,20%,30%) !important;
  background-color: hsl(260,100%,70%) !important}



/*???????? N‚?µ??N?N‚?°*/
textarea{
  text-align: left !important;
  font-weight:bold;
  font-size:12px;
  font-family:Verdana;
  background-color:hsl(260,100%,15%) !important;
  color: hsl(260,20%,60%) !important;
  max-width:800px !important;
  border: 1px solid hsl(1,0%,0%) !important;}

div.busi{
  background-color: hsl(291,100%,80%) !important;
  border: hsl(291,70%,80%) !important}

div.backlink{background-color:hsl(181,30%,70%) !important;}
div.block{border-top: gray;border-bottom: gray}
div.footer{ 
  background:hsl(30,40%,80%) !important;
  border:hsl(30,90%,80%) !important;
  color:hsl(30,35%,20%) !important;
}
div.header,div.footer a{color:hsl(30,100%,30%) !important;}
div.title{
  background-color:hsl(30,70%,90%) !important;
  border-bottom: hsl(30,40%,80%) !important;
}
.block_hr {border-bottom: hsl(30,90%,80%) !important;}
.block_hrs {border-bottom: hsl(30,40%,80%) !important;width:120px;}
span.page {padding:1px;border: hsl(30,0%,90%) !important;}
span.page_is {border: hsl(121,50%,40%) !important;}

div{
  color:hsl(260,35%,20%) !important;
  background-color:transparent !important;
}

}
