css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bizarre.kiev.ua") {
	body, .tg {
		background: hsl(70, 8%, 15%) !important;
		border: hsl(70, 8%, 70%) !important;
		font-size: 12px !important;
		color: hsl(0, 0%, 100%) !important;
	}
	a {
		color: hsl(60, 100%, 75%) !important;
		font-weight: bold;
	}
	font {
		font-size: 11px !important;
		font-weight: bold !important;
	}
	img {
		border: 0px !important;
	}
	b {
		font-weight: inherit !important;
	}
	u {
		font-weight: bold !important;
		text-decoration: none !important;
	}
	input, text, #input, #text, edit, #edit, scroll, frame[name="inp"] {
		background: hsl(70, 8%, 20%) !important;
		color: hsl(60, 50%, 50%) !important;
		border: 1px solid hsl(70, 8%, 70%) !important;
		border-radius: 5px !important;
		font-weight: bold !important;
	}
	
	/* time */
	a[href^="javascript:parent.ntt"] {
		color: hsl(0, 0%, 45%) !important;
		font-weight: 600 !important;
		font-size: 70% !important;
		font-family: Consolas !important;
	}
	a[href^="javascript:parent.ntt"]:hover {
		color: hsl(0, 100%, 100%) !important;
		font-size: 90% !important;
		font-weight: bolder !important;
		text-decoration: none !important;
	}
	
	/* nik */
	a[href^="javascript:parent.n3t"], a[href^="javascript:parent.n2t"] {
		color: hsl(60, 60%, 30%) !important;
		font-weight: 800 !important;
		font-size: 100% !important;
	}
	a[href^="javascript:parent.n3t"]:hover, a[href^="javascript:parent.n2t"]:hover {
		color: hsl(60, 100%, 50%) !important;
		text-decoration: none !important;
	}
	
	/* colors */
	font[color="#000000"] { color: hsl(0, 0%, 50%) !important; }
	font[color="#000080"] { color: hsl(240, 70%, 60%) !important; }
	font[color="#0000FF"] { color: hsl(240, 100%, 75%) !important; }
	font[color="#006600"] { color: hsl(120, 70%, 40%) !important; }
	font[color="#007799"] { color: hsl(200, 100%, 50%) !important; }
	font[color="#008000"] { color: hsl(120, 90%, 50%) !important; }
	font[color="#008080"] { color: hsl(180, 80%, 50%) !important; }
	font[color="#0095E2"] { color: hsl(200, 70%, 50%) !important; }
	font[color="#00AAAA"] { color: hsl(180, 100%, 75%) !important; }
	font[color="#555555"] { color: hsl(0, 0%, 60%) !important; }
	font[color="#6000C0"] { color: hsl(270, 80%, 50%) !important; }
	font[color="#60A000"] { color: hsl(84, 80%, 50%) !important; }
	font[color="#800000"] { color: hsl(0, 0%, 75%) !important; }
	font[color="#800080"] { color: hsl(300, 80%, 40%) !important; }
	font[color="#808000"] { color: hsl(60, 70%, 40%) !important; }
	font[color="#808080"] { color: hsl(0, 25%, 50%) !important; }
	font[color="#9000C0"] { color: hsl(285, 80%, 50%) !important; }
	font[color="#AAAA00"] { color: hsl(60, 90%, 60%) !important; }
	font[color="#B07070"] { color: hsl(0, 80%, 60%) !important; }
	font[color="#BB0000"] { color: hsl(0, 100%, 75%) !important; }
	font[color="#BB0099"] { color: hsl(310, 100%, 75%) !important; }
	font[color="#CC6600"] { color: hsl(30, 100%, 50%) !important; }
	font[color="#FF00FF"] { color: hsl(300, 100%, 60%) !important; }
	font[color="#FFA500"] { color: hsl(40, 100%, 75%) !important; }
}
