css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("facebook.com") {







#contentCol  { background-color:#FFF3F4 !important; }
.profile .profile_color_bar  { background:#ffeaea url(http://a.imageshack.us/img62/6187/aef3ff34w3.png) top left repeat-x !important; }



#blueBar       { background:#F08178 url(http://a.imageshack.us/img69/6577/photosamonitorko020.png) repeat-x top left !important; }
#pageLogo a    { background:url(http://a.imageshack.us/img514/4171/esfvsrfrfvrsgvdrgt4y4.png) no-repeat scroll -21px 0 !important; }
#headNavOut    { background:#ff9292 url(http://img24.imageshack.us/img24/9663/aefaefr3t4y56urh.png) bottom left no-repeat !important; border-top: solid 1px rgba(0,0,0,0.5) !important;
border-left: solid 1px rgba(0,0,0,0.5) !important;
border-right: solid 1px rgba(0,0,0,0.5) !important;
-webkit-border-top-left-radius: 5px;
-webkit-border-top-right-radius: 5px;
-moz-border-radius-topleft: 5px;
-moz-border-radius-topright: 5px;
border-top-left-radius: 5px;
border-top-right-radius: 5px; }
	



.jewelToggler  { background-image:url(http://a.imageshack.us/img707/2038/sdgvsrdgbdrbrdh.png) !important; }
#jewelAlert:hover, #jewelAlert:focus, #jewelAlert:active,
#jewelMail:hover, #jewelMail:focus, #jewelMail:active,
#jewelRequest:hover, #jewelRequest:focus, #jewelRequest:active   { background-color:#F6B2B4 !important; }
#jewelCase .jewel  { border:none !important; }




#navSearch button  { background:url(http://a.imageshack.us/img707/2038/sdgvsrdgbdrbrdh.png) no-repeat scroll -1px 0 !important; }
#navSearch     { border-color:#F08178 !important; }




#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#DE2B26 !important; color:#ffffff !important; }




.lightblue_box  { background-color:#FCE2E3 !important; border-color:#F6B2B4 !important; }
.commentable_item .ufi_section  { background-color:#FCE2E3 !important; }
textarea, .inputtext, .inputpassword  { border-color:#F6B2B4 !important; }




a  { color:#DE2B26 !important; }
.uiLinkButton input  { color:#2c2c2c !important; }
.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color:#2c2c2c !important; }
button.as_link  { color:#2c2c2c !important; }




.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#F6B2B4 !important; }

.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:#FCE2E3 !important; }
.friends_online_sidebar li.buddy_row a:hover  { background-color:#FCE2E3 !important; }

.unread  { background-color:#F6B2B4 !important; }
.line  { color:#DE2B26 !important; }




#jewelCase .jewelFooter a   { background-color:#FCE2E3 !important; }
#jewelCase .jewelFooter .uiTextSubtitle  { color:#DE2B26 !important;  }

#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color:#DE2B26 !important; }

#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#FCE2E3 !important; border-color:#F08178 !important; }
#jewelCase .jewelBox  { background-color:#FFF3F4 !important; }




.uiHeader h2  { color:#DE2B26 !important; }




.current_stuff td  { background-color:#FFF3F4 !important; border-color:#F6B2B4 !important; }




.UIComposer .UIComposer_Box  { background-color:#FFF3F4 !important; border-color:#F6B2B4 !important; }
.profile .box .box_header  { background-color:#FFF3F4 !important; border-color:#F6B2B4 !important; }




.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ffffff !important; border-color:#F6B2B4 !important; }
.profile .top_bar ul.tabs li a.tab_link  { background-color:#F6B0B2 !important; border-color:#F08178 !important; }
.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#DE2B26 !important; color:#ffffff !important; border-color:#DE2B26 !important; }





#chat_tab_bar .disabled .chat_window .chat_conv  { background-color:#FFF3F4 !important;  }
#chat_tab_bar .chat_header  { background-color:#F08178 !important; border-color:#000000 !important; }
#chat_tab_bar .chat_header_name a  { color:#ffffff !important; }
#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color:#F6B2B4 !important; color:#DE2B26 !important; }
#presence_bar .presence_menu_opts h2 span  { background:#F08178 !important; }
#presence_bar .presence_menu_opts h2  { border-color:#F08178 !important; }
.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background:#FFF3F4 !important; }

#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#F08178 !important; border-color:#F08178 !important; }
#presence .presence_bar_button .inner_button  { border-top:none !important; }
#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ffffff !important; }

#presence .presence_menu_opts  { border-color:#F08178 !important; }




.UIButton_Blue  { background:#DE2B26 !important; border-color:#F08178 !important; }
.UIComposer_InputArea  { border-color:#F6B2B4 !important; }
.uiButtonConfirm  { background:#DE2B26 !important; border-color:#F08178 !important; }
#presence #buddy_list_tab #buddy_count strong  { color:#ffffff !important; }














}
