css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fb1.bitefight.org") {

body {
background: #080808 !important;
}

* {
font-family: Helvetica, "Trebuchet MS", Arial !important;
}

body, p, td {
line-height: 150% !important;
}

.active {
background: url(http://zergall.jogger.pl/files/active.png) !important;
}

#charstats td, #training td, #train_skills td, #keyinfo td, #stats2 td, #skills td, #skills_ov td, #stats td, #stats_ov td, #items td, #settings td, #fightproperties td, #creations td, #content  td, .tdi, p {
background-color: rgba(255,255,255,0.05) !important;
color: #999 !important;
font-size: 14px !important;
padding: 5px !important;
-moz-border-radius: 2px;
}

#content a {
color: #99644c !important;
}

#missionreport {
background: #000 !important;
border: none !important;
}

h2, .tdh {
background: #333 !important;
-moz-border-radius: 5px;
-moz-box-shadow: inset 0 5px 10px rgba(255,255,255,0.3);
font-weight: bold !important;
color: silver !important;
text-shadow: 0 1px 3px #000;
font-size: 13px !important;
}

h3 {
text-shadow: 0 1px 10px #000;
color: rgba(255,255,255,0.3) !important;
}

input[type=button], input[type=submit] {
-moz-border-radius: 10px;
border-width: 0 !important;
background: #3e8818 !important;
padding: 3px 15px !important;
-moz-box-shadow: inset 0 5px 10px rgba(255,255,255,0.3);
color: #fff !important;
font-weight: bold !important;
text-transform: capitalize;
text-shadow: 0 2px 4px #000;
}

select, textarea, input[type=text] {
color: silver !important;
border: 1px gray solid !important;
-moz-border-radius: 3px;
}

input[type=button]:hover, input[type=submit]:hover {

-moz-box-shadow: inset 0 -5px 10px rgba(255,255,255,0.3);
}

}
