css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("indeed.com") {
.slc, .sll, .sjas1, .sj1, #auxCol {display:none !important}
h2.jobtitle, .company {font-weight:bold !important}
#resultsCol > table {width:573px !important}

div.pagination {background-color:#ffd;margin:0;position:fixed;right:0;text-align:center; top:171px;font-size:1%;color:#fff; padding:3px 6px 3px 0 !important}
div.pagination a, div.pagination b, div.pagination span.pn, div.pagination span.np {font-size:16px !important;}
div.pagination a, div.pagination b {font-weight:bold !important; margin:0 3px !important; color:#000 !important;}
div.pagination a {color:#ff0;}

div.pagination a:visited {color:#551A8B !important}
}
