css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Page Break div config */
.autoPagerS[id^="apBreakStart"]
{
  margin:10px 10px !important;
  padding:3px 0 !important;
  color:#333 !important;
  font-size:x-small !important;
  font-weight:bold !important;
  text-align:center !important;
  text-shadow: 0px 0px 1px #FFF !important;
  line-height:15px !important;
  height:15px !important;
  background: radial-gradient(ellipse closest-side at center center, #bbb, rgba(187, 187, 187, 0) ) !important;
  border:0px solid transparent !important;
/*  clear:both !important; */
}
/* input box height adjustment */
.autoPagerS[id^="apBreakStart"] input
{
  line-height: 12px !important;
  font-size: x-small !important;
}

/* Link Color */
.autoPagerS[id^="apBreakStart"] a:link { color:blue !important;text-decoration:none !important; }
.autoPagerS[id^="apBreakStart"] a:hover { text-decoration:underline !important; }

/* Eliminate leading "Page Break by AutoPager" and "Related Searches" */
.autoPagerS[id^="apBreakStart"] a:first-of-type,
.AutoPager_Related
  { display:none !important; }

/* Smaller Icon */
.autoPagerS[id^="apBreakStart"] img 
{
  height:10px !important;
  vertical-align:middle !important; 
  margin:0 4px !important;
  border-width: 0 !important;
}

/* Insert leading "AutoPagered" */
.autoPagerS[id^="apBreakStart"]::before
{
  content: "AutoPagered" !important;
  color:gray !important;
  font-size:xx-small !important;
}

/* i really want this to be global */
