css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org") {
	
        body {
          background: #2C2C2C !important;
          color: #eee !important;
        }
	
	a {
          color: #efefef !important;
        }
	
	hr {
          display: none !important;
        }
	
	#navtop, #navbot {
          background: #2C2C2C;
          color: #000 !important;
          width: 100%;
          text-align: center;
          height: 12pt;
          font-size: 7pt !important;
        }
	
	#navtop *, #navbot * {
          color: #eee;
          font-size: 7pt;
          display: inline;
        }
	
	#navtop a:hover, #navbot a:hover {
          background: #555;
        }
	
	form {
          margin: 1em !important;
          margin-bottom:45px !important;
        }
	
	.inputtext, textarea {
          width: 295px;
          background-color:#DDD !important;
          opacity: 0.99 !important;
        }
	
	.rules {
          display: none;
        }
	
	td.postblock {
          background: #444 !important;
	  border: 1px solid #666 !important;
	  color: #aaa !important;
	}
	
	td.postblock * {
	  font-size: 6pt;
	}
 	
	td.reply, td.replyhl {
	  background-color:#333 !important;
	  border: 1px solid #555 !important;
	  color: #eee !important;
	  -moz-border-radius: 8px;
	}
	
	td.replyhl {
	  background-color:#4B4B4B !important;
	  border: 1px solid #4B4B4B !important;
	  color: #eee !important;
	}
	
	.postername, .filesize, span.filetitle, .filesize a, a.linkmail, a.quotejs, .replytitle, .commentpostername, .omittedposts, .abbr {
	  color: #fff !important;
	  font-size: 8pt;
	}
        
	#hd, small, font[size="1"], font[size="2"], #ft, #filter-button, #option-button, #navtopr, #navbotr, .logo,.logo font b span, .doubledash, img.rotating, input[type="button"] {
	  display: none !important;
	}
	
	.pages {
	  background: #444 !important;
	  border: 1px solid #555 !important;
	}
	
	.pages td {
	  color: #444 !important;
	  font-size: 4pt;
	}
	
	.pages td a, .pages td b {
	  color: #eee !important;
	  font-size: 8pt;
	}
	
	.pages td b {
	  color: #EEE;
	}
	
	.pages input {
	  display: none !important;
	}
	
	.pages td:first-child {
	  display: none;
	}
	
	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select {
	  font-size: 9pt ! important;
	  color: #000 !important;
	  background: #333 !important;
	  border-color: gray !important;
	}
	
	input[type="submit"] {
	  background: #444 !important;
	  color: #aaa !important;
	  border: 1px solid gray;
	  -moz-appearance: none;
	  font-size: 9pt;
	}
	
	th {
	  background: #222 !important;
	}
	
	th font {
	  font-size: 7pt;
	}
	
	input[type="file"] {
	  opacity: 0.99 !important;
	}
	
	td.deletebuttons {
	  font-size: 8pt !important;
	  color:#eee !important;
	}
        
	.unkfunc {
	  color:#228854 !important;
	}
        
	.postertrip {
	  color:#3c5d8a !important;
	}
        
	form a img {
	  padding-bottom:5px !important;
	}
        
	.reply {
	  background-color:#333 !important;
	  border: 1px solid #555 !important;
	  -moz-border-radius:8px !important;
	  padding:4px !important;
	  color:#FFF !important;
	}
        
	.logo {
	  color:#3399FF !important;
	}
        
	div.logo img {
	  display:none !important;
	}
        
	span.filetitle, .replytitle {
	  color:#3399FF !important;
	}
        
	.commentpostername a {
	  color:#3399FF !important;
	}

	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select {
	  background:#DDD !important;
	}

}
