css:
@-moz-document domain('xbox-360.co.za'), url-prefix('http://xbox-360.co.za/') 
{
	html {background:url("http://www.xbox-360.co.za/forum/images/xbox/doc-bg.png") repeat-x scroll 0 0 #BBBBBB !important;}
	body {font-family:Helvetica, Arial, Verdana, Tacoma !important;}
	.body_wrapper {background:none repeat scroll 0 0 #d9e2c6 !important;}
    
	.content a, .signaturecontainer a {color:#7FAD2A !important;font-weight:bold !important;}

	.postbody {background:none repeat scroll 0 0 #FFFFFF !important;}
	.postcontrols div a img {margin-top:-2px !important;}
	.postbit .postrow {padding: 5px 10px 10px !important;}
	h2.posttitle {padding:10px 0 !important;}
	
	.contact {width:550px !important;}
	.userinfo_extra {width:140px !important;}
.userinfo_extra dl {width:140px !important;}
	.username_container {float:none !important;}
.username_container div {display:none;}
	.rank {display:inline-block !important;margin-bottom:5px;vertical-align:top;}
	.usertitle {display:inline-block !important;width:250px;}
	.postuseravatarlink img {margin-right:10px !important;}
	.postbit_reputation {margin-left:5px;text-align:left !important;width:100px;}
.repimg {margin-left:-4px !important;}
.memberaction a strong span {display:inline-block;width:250px;}
.postuseravatarlink img {margin-bottom:5px;}

.smallavatar img{margin:2px;}

.postfoot div, .userinfo_noavatar, .userinfo, .textcontrols .floatcontainer, .postbitlegacy .postfoot .textcontrols a, .postbit .postfoot .textcontrols a, .eventbit .eventfoot .eventcontrols a {background-color:#DDDDDD!important;}

	.signature {border-top:1px solid #999999 !important;/*display:none !important;*/}
	/*.content {margin:0 0 10px !important;}*/
}
