/* ----------------------------------------------
//  name           Userstyles.org Remixed
//  author         Kantti
//  website        kanttii.deviantART.com
//  description    Modern look for Userstyles.org
//                 Idea from Drake
//  Version        1.0
// ----------------------------------------------*/
@namespace url(http://www.w3.org/1999/xhtml);
 @-moz-document url-prefix("http://userstyles.org") , url-prefix("https://userstyles.org") {

  html {
	position: relative !important;
    padding-top: 40px;
     background: url("http://dl.dropbox.com/u/9813327/userstyles%20bg.png") #ADD1E2 repeat-x !important; 
  }
  #logo {
    background: transparent url(chrome://stylish/skin/bigicon.png) center left no-repeat;
    padding-left: 32px;
  }
  li {
    margin-bottom: 2px !important;
  }
  #navigation li { 
    margin: 0 !important;
  }
  .list-average-rating:before {
    content: " " !important;
  }
  .list-average-rating img {
    width: 12px !important;
    height: 14px !important;
  }
  body {
  -moz-box-shadow: 0px 0px 50px rgba(0, 0, 0, 0.7) !important;
  -box-shadow: 0px 0px 50px rgba(0, 0, 0, 0.7) !important;
  -webkit-box-shadow: 0px 0px 50px rgba(0, 0, 0, 0.7) !important;
	min-height: 40em;
	position: relative !important;
	background: white !important;
	font: 75%/170% Segoe UI, Verdana, Helvetica, sans-serif !important;
	padding: 12px 0px 50px 0px !important;
	margin: 0px !important;
	color: #333333 !important;
	width: 65em !important;
	position: relative !important;
	margin: 0px auto 35px auto !important;
	-moz-border-radius: 26px;
  }
  
  #logo {
	display: none !important;
  }

  
  #header-bar {
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 10px 0px !important;
	position: relative !important;
	
	-moz-border-radius: 0px !important;
	border-radius: 0px !important;
  }
  #body::after {
  }
  #account-link {
	color:black !important;
	padding-left:20px !important;
  }
  #trimming {
  }
  #left-col {
	width: 78%;
	margin-top: 11px;
  }
	/* main menu */
	
	/* START OLD STUFF */
		#navigation { 
			list-style-image: none !important;
			margin: 0 0 0 0 !important;
			 /* float: right !important; */
			clear: none !important;
			padding: 0 0 0 0 !important;
		}
		#navigation li {
			display: inline !important;
			margin: 0 !important;
		}
		#navigation a, #navigation a:visited, #navigation .current-page {
			border: 0px solid transparent !important;
			border-bottom: none !important;
			background: none repeat-x left top !important;
			color: white !important;
			padding: 0px 0px 0px 0px !important;
			text-decoration: none !important;
			font-weight: bold !important;
			-moz-border-radius: 0px 0px 0 0 !important;
		}
		#navigation .current-page {
			border-top: 0px solid black !important;
			border-left: 0px solid black !important;
			border-right: 0px solid black !important;
			background: none repeat-x left top !important;
			color: white !important;
		}
		#navigation a:hover { 
			background: none repeat-x left top !important;
		}
		#navigation input:not([type="submit"]):focus {
			background: none repeat-x left top !important;
		}
	/* END OLD STUFF */
	
	
	#navigation {
		margin: 24px 0px 30px -5px !important;
		padding: 0px 20px 0px 20px;
		background: none !important;
		height: 33px !important;
		color: #FFF !important;
		width: 100% !important;
	}
	
	#navigation ul {
		background: none !important;
		float: left !important;
		list-style: none !important;
		display: inline !important;
		margin: 0px 0px 0px 0px !important;
		padding: 0px 0px 0px 0px !important;
		font: bold 12px/100% Segoe UI Verdana, Helvetica, sans-serif !important;
		width: 100% !important;
	}
	#navigation li {
		background: none !important;
		float: left !important;
		width: auto !important;
		height: 33px !important;
		margin: 0px 1px 0px 0px !important;
		padding: 0px !important;
		display: inline !important;
	}
	.current-page {
		color: white !important;
		background: none !important;
	}
	#navigation li a, #navigation li span {
		width: auto !important;
		height: 33px !important;
		margin: 6px 7px 13px 7px !important;
		text-decoration: none !important;
		color: #FFF !important;
		display: block !important;
	}
	#navigation li a:visited {
		text-decoration: none !important;
		color: white !important;
	}
	#navigation li:hover {
		text-decoration: none !important;
		background: #a2d0dd !important;
		color: black !important;
	}
	
	#search{ 
		margin: 15px 0px 0px 0px !important;
	}
	
	.post-your-style, #sidebar {
		padding: 0px 15px 0px 0px !important;
	}
	p, h1, h2, h3, #screenshots, #style-meta, form {
		padding-left: 15px !important;
	}

	#style_long_description, #css, .linking-note{
		width:600px !important;
	}
	#breadcrumbs { 
		padding-top:0px !important;
		background: rgba(115, 160, 85, 1) url("http://dl.dropbox.com/u/9813327/userstyles%20logo.png") left no-repeat !important;
		color:black !important;
		font-size: smaller !important;
		padding-bottom: 70px !important;
		padding-left:375px !important;
		margin: 5px !important;
		background-color:white !important;
	}
	#breadcrumbs a, #other-links a {
		background: rgba(115, 160, 85, 1) !important;
		padding: 3px 3px 3px 3px !important;
		-moz-border-radius: 3px !important;
		color:white !important;
	}
	#breadcrumbs a:hover, #other-links a:hover {
		-moz-border-radius: 3px !important;
		border-radius: 3px !important;
		-moz-box-shadow: inset 0px 0px 10px -2px black !important;
		box-shadow: inset 0px 0px 10px -2px black !important;
		background: rgba(115, 160, 85, 1) !important;
		color:white !important;
	}
	h1 {
		-moz-border-radius: 3px !important;
		border-radius: 3px !important;
		-moz-box-shadow: inset 0px 0px 10px -2px black !important;
		box-shadow: inset 0px 0px 10px -2px black !important;
		font: 170% Segoe UI, Verdana, Helvetica, sans-serif !important;
		text-shadow: 0px 0px 6px black !important;
		background: rgba(115, 160, 85, 1) !important;
		margin-left: 6px !important;
		padding: 4px 0px 4px 15px !important;
		margin-right: 6px !important;
		margin-bottom: 6px !important;
		margin-top: 6px !important;
		/* font-variant: small-caps !important; */
		color:white;
	}
	#install {
		-moz-border-radius: 3px !important;
		border-radius: 3px !important;
		-moz-box-shadow: inset 0px 0px 10px -2px black !important;
		box-shadow: inset 0px 0px 10px -2px black !important;
		font: 100% Segoe UI, Verdana, Helvetica, sans-serif !important;
		text-shadow: 0px 0px 6px black !important;
		background: rgba(115, 160, 85, 1) !important;
		margin-left: 6px !important;
		margin-right: 6px !important;
		margin-bottom: 6px !important;
		margin-top: 6px !important;
		color:white;
		border: 0 !important;
	}
	#other-links {
		float: left !important;
		background: rgba(115, 160, 85, 1) !important;
		margin: 40px 15px 0px 0px !important;
		padding: 10px 10px 10px 10px !important;
		-moz-border-radius: 3px !important;
		border-radius: 3px !important;
		-moz-box-shadow: inset 0px 0px 10px -2px black !important;
		box-shadow: inset 0px 0px 10px -2px black !important;
		left: 375px !important;
	}
	#search {
		position: absolute !important;
		right: 15px !important;
		top:-5px !important;
	}
	.multiple-screenshot, .single-screenshot {
		max-width: 700px !important;
		display: block !important;
		position:relative !important;
	}

	.screenshot-table, .screenshot-table td, #screenshots {
		margin-left:auto !important;
		margin-right:auto !important;
		text-align:center !important;
		width: 700px !important;
		max-width: 700px !important;
		vertical-align: top !important;
		height:auto !important;
		list-style: none !important;
		display: block !important;
	}
        TR:not(:hover) TD[class="screenshot-after"] {
                display:none !important;
        }
        TR:hover TD[class="screenshot-before"] {
                display:none !important;
        }	
        TR TD[class="screenshot-after"] {
                color:blue !important;
        }
        TR:active  TD[class="screenshot-after"], TR:active TD[class="screenshot-before"] {
                display: -moz-box !important;
        }

}
