css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com") {

body {
margin: 0 !important;
padding: 0 !important;
}

#splashPosts, #ad-300x250, div[class="smalltype"][id="sidebar"][style="border-top-style: solid; border-top-width: 1px;"], #sidebar, #skyscraper, #skySpacer, #ad_160x160, .ad_728x90, #footer, #logo, .ad_themeweek-1000px {
display: none !important;
visibility: hidden !important;
}

#menubar-bg, #topMenu, #popTags {
width: 99% !important;
text-align: right !important;
}

#header {
height: 60px !important;
}

#header, #container, #menubar {
background: none !important;
border: none !important;
width: 99% !important;
}

#wrapper {
width: 100% !important;
margin: 0 !important;
padding: 0 !important;
}

.post.siteid_17 {
width: 100% !important;
}

.content.permalink {
float: left !important;
font-size: 16px !important;
line-height: 26px !important;
width: 70% !important;
margin: 0 !important;
padding: 0 !important;
}

.postmeta_permalink_wrapper {
float: right !important;
margin: 0 !important;
padding: 0 !important;
}

span.left, span.right {
display: none !important;
}

#search_form {
background: none !important;
}

input {
font-size: 14px !important;
height: 16px !important;
background: none !important;
border: 4px solid black !important;
width: 200px !important;
}

}
