css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document url-prefix("http://www.jeuxvideo.com/profil/") {
 
.rubis #cdv_header, .or #cdv_header, .argent #cdv_header, .bronze #cdv_header, .carton #cdv_header {
background: #DDD
url(http://image.jeuxvideo.com/css_img/defaut/cdv/header_diamant.png) left top no-repeat !important;
}
 
.rubis li#rang span, .rubis #td_rang strong, #liste_rangs #rubis td,
.or li#rang span, .or #td_rang strong, #liste_rangs #or td,
.argent li#rang span, .argent #td_rang strong, #liste_rangs #argent td,
.bronze li#rang span, .bronze #td_rang strong, #liste_rangs #bronze td,
.carton li#rang span, .carton #td_rang strong, #liste_rangs #carton td {
background-position: left -150px !important;
}
.rubis #onglets li, .rubis #onglets li a,
.or #onglets li, .or #onglets li a,
.argent #onglets li, .argent #onglets li a,
.bronze #onglets li, .bronze #onglets li a,
.carton #onglets li, .carton #onglets li a {
background-position: left -180px !important;
color: #0E5C7D !important;
}
.rubis #cdv_page, .or #cdv_page, .argent #cdv_page, .bronze #cdv_page, .carton #cdv_page {
border-color: #DDF2FB !important;
}
 
}
