@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thepiratebay.sx") {

/* front page ads */
#topleft, #topright { display:none !important; }

/* muahah too easy - removes all the ads */
iframe { display:none !important; }

/* Tool Bar Ad On Torrent Page */
a[href="http://radiobar.toolbarhome.com/download.aspx"] { display: none !important; }

/* Another Toolbar Ad */
IMG[src*="thepiratebay.sx/img/bar.gif"], A[href*="sweetim.com"] { display: none !important; }

/* "WATCH ONLINE FOR FREE ON TUBE+" */
a[href="http://tubeplus.me/"] { display: none !important; }

/* Damn You Frame Ads */
#a68d4628 { display: none !important; }
#a0ef4652 { display: none !important; }
#a7e1cf36 { display: none !important; }
#a8e6d294 { display: none !important; }
#a72537a4 { display: none !important; }
#a89ffaf5 { display: none !important; }

/* "Your browser does not support direct torrents downloads" "Install Plug-in" */
div[id*="hitchhacker"]  { display: none !important; }

/* Anonymous Download */
a[href*="freevpn.thepiratebay.sx"], a[href*="privitize.com"], a[href*="adexprt.me"], a[href*="adexprt.com"]  { display: none !important; }

/* "Problems with magnets links are fixed by upgrading your torrent client" */
a[href*="torchbrowser.com"] { display: none !important; }

/* Sponsored Links */
#sponsoredLinks, .spons-link { display: none !important; }

/* Just In Case I Missed One */
#sky-banner, .ads, .ad { display: none !important; }

/* Right Side Bar Ad On User Pages */
#sky-right { display: none !important; }

/* Hides NoScript Place Holder Icons */
[class="__noscriptPlaceholder__ "], .__noscriptPlaceholder__.__noscriptObjectPatchMe__ { display: none !important; }

}
