@namespace url(http://www.w3.org/1999/xhtml); 

/* BrandNew&EggXited! */
/* Newegg.com */
/* EggXpert.com
/* Xavian-Anderson Macpherson */
/* http://userstyles.org/users/3665 */
/* Shingoshi@Gmail.com */
/* NASA Night Launch Specific Modifications */
/* https://addons.mozilla.org/en-US/firefox/addon/4908 */

/*=========================================*/
@-moz-document domain("newegg.com"),
                  domain("eggxpert.com"),
                  domain("foreseeresults.com")
 {
/*=========================================*/


/*=========================================*/
/* Menu Colors */
#haTabStoreMenu ul li a,

[class="cartMenu innerBtn"],
[class="cartSubtotal cartHeader"] *,
[id="line1"]
 {
 color: #D2D27D !important;
 background-color: #33425a !important;
 border-color: #000 !important;
 font-size: 10pt !important;
 }
/*=========================================*/


/*=========================================*/
/* Left SideBar */
 #haTabStoreMenu ul li,
 #navTabStore ul li a:link,

 [class="categoryList"],
 [class="CommonSearchBox"],
 [class="footer"],
 [class="light"],
 [class="reviewPageArea"]
 {
 background-color: #222c3c !important;
 }
/*=========================================*/


/*=========================================*/
[class="cartChartH"],
[class="cartTotal cartHeader"] *
 {
 color: #bbb !important;
 background-color: #222c3c !important;
 }
/*=========================================*/


/*=========================================*/
/* Wish Lists */
 tr.cartChartEven td { color: #a55 !important; }
 tr.cartChartOdd td { color: #bb6 !important; }

[class="cartChartEven"],
[class='CookieChartEven']
 { color: #aa5 !important; background-color: #33425a !important; }

[class="cartChartOdd"],
[class='CookieChartOdd']
 { color: #cc7 !important; background-color: #43526a !important; }

[class="noteSavings"] { color: rgb(5,215,20) !important; }
[class="txtRed"]
 { color: #f44 !important;
 font-weight: bolder !important;
 font-size: 16px !important;
 }
/* WakeUp! */
/*=========================================*/


/*=========================================*/
 [class="content"]
 {
 color: #556e96 !important;
 background-color: #000 !important;
 }
/*=========================================*/


/*=========================================*/
/* Advanced Search */
#bcasItemList .cartTbl td,
select,

[class="cartCalc innerBtn"],
[class="cartDescription"],
[class="cartItem"],
[class="cartPrice"],
[class="cartQty"],
[class="cartSavings"],
[class="cartSelect"],
[class="cartShipping cartHeader"],
[class="cartShipping cartHeader"] td,
[class="searchKeywords"],

[id="SrchInDesc_bottom"],
[id="SrchInDesc_top"],
[id="text1"]
 {
 color: #ccc !important;
 background-color: #171717 !important;
 border-color: #333 !important;
 font-weight: normal !important;
 margin-bottom: 4px !important;
 }
[class="bodyCenterArea"] { margin-left: 275px !important; }
[id="bodyLeftArea"] { width: 200px !important; }
/*=========================================*/


/*=========================================*/
 .reviewSummary,
[id="bcaShopWindowSEO"]
 {
 background-color: #222 !important;
 border-width: 1px !important;
 }
/*=========================================*/


/*=========================================*/
/* Standard Fields */
 .comboCell td,
 .CommonSidebarContentItem,
 #awards img,
 #bodyCenterArea,
 #footerArea,
 #footerCorner .center,
 #haInfoBarCart,
 #haInfoBarMyAccount,

 INPUT, TR,
 body,
 p,
/* td[style=""],*/
 tr.optionsTop td,
 tr.cartChartOdd td,

 [class="advSearchList"],
 [class="BlogPostContent"],
 [class="body"],
 [class="bodyCenterArea"],
 [class="bodyLeftArea"],
 [class="cell"],
 [class="cell crb"],
 [class="center"],
 [class="comboCell"],
 [class="Common"],
 [class="CommonContent"],
 [class="CommonContentArea"],
 [class="CommonSidebar"],
 [class="CommonSidebarArea"],
 [class="CommonSidebarContent"],
 [class="CommonSidebarFooter"],
 [class="CommonSidebarHeader"],
 [class="CommonSimpleTabStripTab"],
 [class="CommonSimpleTabStripSelectedTab"],
 [class="CommonTabBarInner"],
 [class="contInf"],
 [class="dark"],
 [class="description"],
 [class="dropdownlist"],
 [class="gdSearchList"],
 [class="header"],
 [class="imgSlide"],
 [class="left"],
 [class="noline"],
 [class="right"],
 [class="showImgBottom"],
 [class="showImgCur"],
 [class="showImgItemTitle"],
 [class="space"] *,

 [id="ATTR1"],
 [id="awards"],
 [id="bcaProductCell"],
 [id="bcaShopWindowFooter"],
 [id="bcaShopWindowHeader"],
 [id="centerArea2"],
 [id="Common"],
 [id="CommonBodyColumn"],
 [id="CommonFooter"],
 [id="CommonLeftColumn"],
 [id="CommonRightColumn"],
 [id="container"],
 [id="CurrentImage"],
 [id="disclaim"],
 [id="disclaimer"],
 [id="footerArea"],
 [id="footercomb"],
 [id="GiftCode"],
 [id="haInfoBarCenter"],
 [id="haQuickSearchBox"],
 [id="haQuickSearchInfoTab"]
 {
  color: #aaa !important;
  background-color: #000 !important;
  background-image: none !important;
  border-color: #222 !important;
  border: 1px !important;
 }
/* Now Plowed! */
/*=========================================*/


/*=========================================*/
/* Links and other Text */
 #productPrint a:link, a:visited, a:hover, a:active,
 div,
[class="savings"]
 {
 color: #ccc !important;
 }

[class="green"],
[class="shipping"]
 {
 color: #5c5 !important;
/* font-weight: bold !important;*/
 }

 #blaNavigation .gdSearchList a:link,
 a:link,
 [class="stockInfo"]
 {
 color: #aa5 !important;
 font-weight: normal !important;
 }

/* Red Text */
[class="promotionText"]
 {
 color: #C83C78 !important;
 }
/* Free at Last! */
/*=========================================*/


/*=========================================*/
/* Search Box */
[class="CommonSearchBox"]
 {
 background-color: #000 !important;
 background-image: none !important;
 }
/*=========================================*/


/*=========================================*/
/* White Borders */
table img,
*|*:-moz-any-link img, img[usemap], object[object]
 {
 border-left-color: #fff !important;
 border-right-color: #fff !important;
 border-top-color: #fff !important;
 border-bottom-color: #fff !important;
 }
/* Dingy Jeans! */

/*=========================================*/
/* Menu Background */
#haTabStoreMenu ul li a:hover
 {
 background-color: #333 !important;
 }
/*=========================================*/


/*=========================================*/
/* It All Ends Here! */
}
