css:
@-moz-document domain("board.ogame.de") {body {
background:none!important;
color: #fff!important;
font-weight:400!important;
font-family: MS-Sans, "Helvetica Neue", Helvetica, Arial, sans-serif!important;
letter-spacing: 1px;
line-height:1.5;
}
html {
background: #000 url(http://savekb.de/dgnbg.jpg) right -20px no-repeat fixed!important;
}

.tableList tbody .container-1:last-child {-webkit-border-radius-bottomleft: 10px; -webkit-border-radius-bottomright: 10px;}

.infoBox {-webkit-border-radius:10px;background: rgba(255,255,255,0.35)!important;}
.infoBox div {background:none!important;}

.message {border:none!important;}
.messageContentInner {-webkit-border-radius:10px;}

#logo {
display:none!important;
}
#search {margin-top:15px}

.messageInner, form .content .container-1, form .content .container-2 {-webkit-border-radius:10px;}
/* 
.messageSidebar {float:right!important;}
.messageContent {margin: 10px 180px 10px 10px!important;}
*/

.containerHead {-webkit-border-radius-topleft:10px; -webkit-border-radius-topright:10px;}

.tableList tbody .container-1:last-child {-webkit-border-radius-bottomleft: 10px; -webkit-border-radius-bottomright: 10px;}

.message {border:none!important;}
.messageContentInner {-webkit-border-radius:10px;}



a:hover {font-weight:normal;}

.pageNavigation li a:hover {text-decoration:none!important;margin-left:1px!important;}

#searchInput {width:100%!important;-webkit-border-radius:5px;}

#search {width:25%!important;}

#searchSubmit {position:absolute;top:50%;right:0!important;margin-right:-10px!important;margin-top:-7px!important}

.searchContainer {right:0!important;}

.breadCrumbs {width:75%;z-index:1000!important;}


.pageNavigation ul li a, .pageNavigation ul li span {-webkit-border-radius:5px;padding:2px 5px!important;}

.pageNavigation ul li {-webkit-border-radius:5px;}

.pageNavigation ul li>a:hover {margin-left:0px!important;}

select {-webkit-border-radius:5px;background:rgba(0,0,0,0.25)!important}

.info {-webkit-border-radius:5px;}

#tabContent {margin-top:-10px!important;}

.subTabMenu {border:none!important;}

.tabMenu li a {-webkit-border-radius-topleft:5px;-webkit-border-radius-topright:5px;}

.messageContentInner .signature {border:none!important}

.messageFooterRight li {border:none!important;}

.messageFooterRight li a {border:1px solid rgba(255,255,255,0.2)!important; -webkit-border-radius:5px;padding:0 4px 2px 4px!important}
.message .messageContentInner {padding-left:0px!important;padding-right:0px!important;} 

.content>.messageInner {padding-left:0px!important;padding-right:0px!important; margin-left:0!important;margin-right:0!important}

.messageContentInner .messageHeader, .messageContentInner h3, .messageContentInner .messageBody, .messageContentInner .signature, .messageContentInner .editNote, .messageContentInner, .messageFooterRight {padding-left:10px!important;padding-right:10px!important;border-color: #171E2F!important} 

.messageBody, .messageFooter, .messageHeader, .messageInner h4 {padding-left:25px!important;padding-right:25px!important;border-color: #171E2F!important}

.messagePreview .message {margin-top:-10px!important}



.messageBody {width:auto!important;}


.inputText {-webkit-border-radius:5px;}

.largeButtons li, .largeButtons li a, .memberList li .memberName, .messageNumber, .optionButtons a, .smallButtons a {-webkit-border-radius:10px;background: rgba(0,0,0,0.1)!important}

.mceResizeIconRow .container-1 {-webkit-border-radius-topleft: 0!important;-webkit-border-radius-topright: 0!important;margin-top:-26px!important}

.messageInner {background: transparent!important;}

.userRank {display:none;}

.userMessenger {display:none;}

.userCredits p {display:none;}

.userCredits .userPosts, .userCredits .userRegistration {display:block;font-size:80%}

.signature:before {
content: "~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~";
font-family: Georgia, serif;
letter-spacing: -1px;
text-align:center;
display:block;
margin: 10px auto;
color: rgba(255,255,255,1);
}

a, a:link, a:hover, a:active, a:focus {
font-weight:500!important;
}


* {
text-shadow: 0 -1px 1px rgba(0,0,0,0.5)!important;
}

::-webkit-selection {
background: hsla(320,90%,40%,0.4);
-webkit-border-radius:2px;
}

.boardlistInner {-webkit-border-radius:10px;}
#boardlist li {-webkit-border-radius:10px;background:none!important;}
.boardlistInner .containerIcon {display:none!important}
.boardlistInner .containerContent {margin-left:0!important}

a:hover {color:#fff!important;}

a:active {position:relative;top:1px;color:hsl(320,100%,50%)!important}

.container-1 fieldset, .container-1 fieldset fieldset.noJavaScript fieldset, .container-2, .container-2 fieldset fieldset, .container-3 fieldset, .messageInner .color-2, .weekDays, fieldset fieldset{background: rgba(255,255,255,0.25)!important}
.container-1, .container-1 fieldset fieldset, .container-2 fieldset, .inlineCalendarTable, .messageInner .color-1, .pageNavigation li ul, fieldset{background: rgba(255,255,255,0.3)!important}
legend {color:#000!important}

.tableList .container-1, .tableList .container-2 {color: #fff!important;}
.tableList a {color: hsl(45,90%,50%)!important;}

.messageContentInner {
background: rgba(255,255,255,0.2)!important;
color: #151515!important;}

.tabMenu li a {background:rgba(255,255,255,0.5)!important;color:#151515!important;}
.tabMenu li {border:none!important;}

.message a {
color:#ff0054!important;
text-shadow: 0px 0px 7px rgba(0,0,0,0.25),0 -1px 1px rgba(0,0,0,0.75)!important;
}

.containerHead {background:rgba(255,255,255,0.5)!important}

.subTabMenu .containerHead {background:transparent!important;}
#tabContent div, #tabContent {-webkit-border-radius:10px}

.quoteBox {-webkit-border-radius:10px;background: rgba(0,0,0,0.5)!important}

.tableHead th a, .containerHead {background: rgba(255,255,255,0.17)!important;border-bottom:1px solid rgba(255,255,255,0.1)!important}
.tableHead, .tableHead th {background:transparent!important;}

.messageHeader .containerContent p {color: #151515!important;}

textarea {background:rgba(0,0,0,0.5)!important;-webkit-border-radius:10px 10px 10px 10px;padding:5px 5px 31px!important;width:100%!important;-webkit-box-sizing:border-box;}

.inputText {background:rgba(0,0,0,0.5)!important;}

.pageNavigation li, .pageNavigation li a, .pageNavigation li span {background:rgba(0,0,0,0.25)!important;border:1px solid rgba(255,255,255,0.25)!important;-webkit-border-radius:5px!important}

.pageNavigation .skip, .pageNavigation .skip a {background:none!important;border:none!important}

.pageNavigation .active {background: rgba(0,0,0,0.5)!important;border:1px solid hsl(320,60%,30%)!important}

.editNote {color:#fff!important;}
.codeBox {background: rgba(0,0,0,0.5)!important;-webkit-border-radius:10px}
.codeBox td {background:none!important}

#searchInput {background: url(http://board.ogame.de/wcf/icon/searchHeadS.png) 2px center no-repeat!important;}

.pageMenu a {
background: rgba(255,255,255,0.25)!important;
}

#optionsearchAuthor a {
color: #222!important;
}

#optionsearchAuthor {
background: rgba(255,255,255,0.5)!important;
-webkit-border-radius: 5px;
}


}
