css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ganjatalk.com") {

body, hr {
background: #333 url("data:image/png;base64,") top left repeat-x !important;
color:#fff !important;

}

img[src="images/xmbforum/ganjalogo_sm.jpg"] {
    width: 0 !important;
    height: 85px !important;
    padding-left: 366px !important;
    background-image: url("http://img146.imageshack.us/img146/1821/ganjatalkdarknm1.gif");
background-position: top left;
    background-repeat: no-repeat;
  }



.advlink {color:#fff! important;}

table table td  {
background-color:#505050 !important;
padding:0px ;
border-collapse: collapse !important; 
}

table table td a img{
background-color: #929292 !important;
}

table table a {
color: #000 !important;
text-decoration: none !important;
}

a:hover {
text-decoration: underline !important;
color:#fff !important;
}

/* Suject IN post */
.subject {
font-size: 10px !important;
font-family: Verdana !important;
font-weight: bold !important;
}

/* new top, new poll, post reply buttons */
.post {
background-color: #121212 !important;
font-size: 12px !important;
font-weight: normal !important;
font-family: Verdana !important;

}

/* post header */
.header {
color: #FFFFFF !important;
background-color: #333 !important;
font-family: Verdana !important;
font-weight: bold !important;
font-size: 10px !important;
}

.header td {
color: #FFFFFF !important;
background-color: #333 !important;
font-family: Verdana !important;
font-weight: bold !important;
font-size: 10px !important;
}

/* I'm Not really sure what this belongs to */
.header2 {
background-color: #515151 !important;
font-family: Verdana !important;
font-weight: bold !important;
font-size: 11px !important;
}

/* Category on Main forum Page */
.category, .category a font {
background-color: #515151 !important;
font-family: Verdana !important;
font-size: 10px !important;
color:#ffffff !important;
}

.tablerow {
background-color: #808080 !important;
font-family: Verdana !important;
color: #dadada !important;
font-size: 10px !important;
}
.tablerow td{
background-color: #808080 !important;
font-family: Verdana !important;
color: #dadada !important;
font-size: 10px !important;
}

.tablerow a{
font-family: Verdana !important;
color: #fff !important;
font-size: 10px !important;
}

.tablerow font a {font-weight: bold !important;}

/* breadcrumbs */
.nav {
background-color: #a1a1a1 !important;
font-family: Verdana !important;
font-weight: bold !important;
font-size: 10px !important;
color:#101010 !important;
width:100% !important;
}
.nav a{ color:#000 !important;}

/* Last active, Loggin, Control Panel/Option  */
/* Also is the text on forum.index.php  */
.smalltxt {
font-size: 9px !important;
font-family: Verdana !important;
color:#fff !important;
}
.smalltxt a {color:#fff !important;}

.smalltxt u {color:#fff !important;}

/* general txt in post and subject in post list */
.mediumtxt {
font-size: 10px !important;
font-family: Verdana !important;
font-weight: normal !important;
color: #ddd !important;
}

/* Nav Bar - Member list | Search | FAQ | Todays Post| Stats */
.navtd, .navtd td {
font-size: 11px !important;
font-family: Verdana !important;
color: #FFFFFF !important;
background-color: #7A7A7A !important;
text-decoration: none !important;
}

/* in viewthread.php - Printable version | Send to Friend |subscribe |add to Favoites */
.multi, .multi a {
background-color: #121212 !important;
font-family: verdana !important;
font-size: 11px !important;
color:#fff !important;
}

textarea, select, input, object {
font-family: Verdana, arial, helvetica, sans-serif !important; 
font-size: 12px !important; 
font-weight: normal !important; 
background-color: #C8C8C8 !important;
color: #000000 !important; 
}
textarea {
width:500px;
height:600px;
background-color:#fff;
}


}
