css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
*{
font-family:tahoma !important;
letter-spacing:.1px;
}

#sr-header-area {
background:white !important;
}

.thumbnail {
margin-top:-5px !important;
padding-bottom:2px !important;
}
.tabmenu a {
-moz-border-radius:4px 4px 0px 0px;
-webkit-border-radius:4px 4px 0px 0px;
}

.entry {border:none !important}
.link, .md {


border-width:0;
border-color:rgba(0,0,0,.1) !important;
border-style:solid !important;
background:-moz-linear-gradient(#fff,#fafafa);
background: -webkit-gradient(linear, left top, left bottom, from(#fff), to(#fafafa));
border-bottom:1px;
border-left:1px solid rgba(0,0,0,.05) !important;
}
.midcol {
padding-top:10px !important;

}
.link {
margin:0 !important;
margin-top:2px !important;
}
.link .entry {
-moz-border-radius:20px;
background:-moz-linear-gradient(#fafafa,#fff);
-webkit-border-radius:20px;
background: -webkit-gradient(linear, left top, left bottom, from(#fafafa), to(#fff));
padding:0px 0px 0px 20px !important;

margin-right:25px !important;

}
.content {
margin:0 !important;

padding-left:5px !important;
padding-right:5px !important;
padding-top:5px !important;
}
.side .content {


background:none !important;
}

.side {

background:transparent -moz-linear-gradient(left, rgba(255,255,255,0), #fff 100px) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#fff), to(#fff));

padding-left:100px !important;

}


.author {
font-weight:bold;
text-shadow:1px 1px 0px rgba(255,255,255,1);
}
.submitter {
display:inline-block;
margin:2px 0px 0px 2px;
-moz-box-shadow:2px 2px 4px rgba(0,0,0,.3);
-moz-border-radius:3px;
-webkit-box-shadow:2px 2px 4px rgba(0,0,0,.3);
-webkit-border-radius:3px;
  font-size:1.2em;
}

div, td, span {
-moz-border-radius:5px;
-webkit-border-radius:5px;
}

.nestedlisting .md {
background:none !important;
border:none !important;
}
.commentarea .entry {
-moz-border-radius:0;
-webkit-border-radius:0;
margin-top:25px !important;
}

.child .entry {
margin-top:10px !important;
}
.child {
border-left:1px solid #999!important;
}
.organic-listing {
border-color:transparent !important;
}
.organic-listing .link{
-moz-border-radius:0;
-webkit-border-radius:0;
border:1px solid rgba(0,50,50,.3) !important;
}

.arrow img {
-moz-box-shadow:0px 0px 10px white,0px 0px 10px white,0px 0px 10px white ;
-webkit-box-shadow:0px 0px 10px white,0px 0px 10px white,0px 0px 10px white ;
}
.arrow {height: 15px !important;opacity:.9;}
.arrow.up {background: url(data:image/png;base64,) no-repeat 0 0 !important;} 
.arrow.down {background: url(data:image/png;base64,) no-repeat 0 0 !important;}
.arrow.upmod {background: url(data:image/gif;base64,) no-repeat 0 -31px !important;}
.arrow.downmod {background: url(data:image/gif;base64,) no-repeat 0 -16px !important;}
}
