css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.google.com/"), 
               url-prefix("http://www.google.com/webhp"),
               url-prefix("http://www.google.com/imghp")
{

#ghead/*, #gog*/ {
  background-color: rgba(255, 255, 255, 0.6) !important;
/*  border-bottom: 1px solid rgba(201, 215, 241, 0.5);*/
  -webkit-box-shadow: 0px -16px 48px black,
                      0px -12px 32px black;
  -moz-box-shadow: 0px -16px 48px black,
                   0px -12px 32px black;
}
.ds, .gac_od, .lst {
/*  border: 1px solid rgba(0, 2, 2, 0.125) !important;
  -webkit-border-radius: 4px;  /* Chrome *
  -moz-border-radius: 4px;     /* Firefox *
  border-radius: 4px;          /* Opera */
}
.ds *:focus, .ds:active, .ds:hover {
  -webkit-box-shadow: 0px 0px 8px lightblue;
  -moz-box-shadow: 0px 0px 8px lightblue;
}


/* "Remove background image" at bottom */

#cpNavTextWrapper {
  padding: 4px;
  background-color: rgba(255, 255, 255, 0.25) !important;
  border: thin solid ThreeDShadow;
  -moz-border-radius: 3px;
}
#cpNavTextWrapper:hover {
  background-color: rgba(255, 255, 255, 0.125) !important;
  border-color: highlight;
}

}
