css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") { }

@-moz-document url-prefix('http://userstyles.org/users') , url-prefix("https://userstyles.org/users") {
  body > h2 {
    margin-bottom:  -1.75ex  !important;
    border-bottom:  solid 1px #cdd;
  }
}

@-moz-document url-prefix('http://userstyles.org/styles') , url-prefix("https://userstyles.org/styles") {
  form > .form-controls table table textarea {
    white-space:  nowrap  !important;
    overflow-x:   scroll;
    min-width:    20ex;
  }
}

@-moz-document domain("forum.userstyles.org") {
  /* Remove double-spacing on PRE-formatted elements */
  div.Preview    div.Message code br,
  div.Preview    div.Message pre  br,
  ul.MessageList div.Message code br,
  ul.MessageList div.Message pre  br{
    display:  none;
  }
}


@-moz-document url-prefix("http://userstyles.org") , url-prefix("https://userstyles.org") {
  * {
    font-size:  100% !important;
  }
  body > *,
  body {
    margin: 0 !important;
  }
  body {
    font-family:  'Gill Sans','Gill Sans MT','Helvetica Neue','Trebuchet MS',Arial,sans-serif  !important;
    background:   #fff  !important;
    font-size:    small !important;
    color:        #333  !important;
  }

  body h3,
  body h2,
  body h1 {
    margin-bottom:  1ex  !important;
  }

  a {
    color: #579  !important;
  }
  a:visited {
    color: #975  !important;
  }
  a:active,
  a:focus,
  a:hover {
    text-decoration:  none  !important;
  }

  ul {
    color:  #abc  !important;
  }
  ul > li {
    list-style-type:  square;
  }
  ul ul > li {
    list-style-type:  circle;
  }
  ul ul ul > li {
    list-style-type:  disc;
  }

  body > h1 ~ *:not(#overlay):not(#lightbox) {
    font-size: 100%      !important;
    padding:   0 0px     !important;
    margin:    20px auto !important;
    min-width: 560px;
    width:     66%       !important;
  }


  #main-header {
    background-color: transparent  !important;
  }
  #main-header a {
    color: #ffc  !important;
  }

  #content-wrapper {
    margin: 0 0 0 200px !important;
    float:  none        !important;
    width:  auto        !important;
  }
  #left-sidebar,
  #main-article {
    margin-left:  0  !important;
  }
  #left-sidebar {
    position: absolute;
    left:     10px;
    top:      10ex;
  }
  #left-sidebar > * {
    background-color: #e1eeee  !important;
    box-shadow:     0 2px 8px rgba(0,0,0,.25);
    padding:        5px 10px   !important;
    margin:         0 0 10px 0 !important;
    border:         solid 4px #fff;
    border-radius:  0          !important;
  }


  #other-links,
  #breadcrumbs {
    line-height:  200%     !important;
    background:   #68a     !important;
    font-size:    90%      !important;
    padding:      2px 20px !important;
    margin:       0        !important;
    width:        auto     !important;
    color:        #aac     !important;
  }
  #other-links {
    background:     transparent !important;
    border-bottom:  none        !important;
  }
  #other-links > a,
  #breadcrumbs > a {
    color:  #ccf  !important;
  }
  #other-links > #search {
    padding-left: 10px;
    margin-left:  5px;
    border-left:  solid 1px rgba(255,255,255,.25);
  }
  h3,
  h2,
  h1 {
    letter-spacing: 0.125ex;
    margin-bottom:  3ex     !important;
    font-variant:   normal  !important;
    line-height:    175%;
    font-weight:    200;
  }
  h1 {
    font-family:  Georgia,serif  !important;
    background:   #579    !important;
    font-style:   italic;
    font-size:    200%    !important;
    padding:      0 20px  !important;
    margin:       0 0 2ex !important;
    color:        #fff    !important;
    border-top:   solid 1px rgba(255,255,255,.25);
   -webkit-box-shadow:  -9px 4px 9px rgba(0,0,0,.25);
   -moz-box-shadow:     -9px 4px 9px rgba(0,0,0,.25);
    box-shadow:         -9px 4px 9px rgba(0,0,0,.25);
  }
  body > h2 {
    letter-spacing: 0.5ex;
    margin-bottom:  0ex   !important;
    font-style:     italic;
    font-size:      150%  !important;
    color:          #789  !important;
    text-shadow:    1px 1px 2px #999;
  }

  
  #style-meta {
    padding-bottom: 15px;
    font-style:     italic;
    color:          #999;
    border-bottom:  2px dotted #ccc;
  }

  #screenshots {
    overflow: auto;
    padding:  0   !important;
    width:    auto;

    background: rgba(0,0,0,.01);
    border:     inset 1px rgba(0,0,0,.125)  !important;
   -webkit-box-shadow: inset 0px 1px 3px rgba(0,0,0,.125);
   -moz-box-shadow:    inset 0px 1px 3px rgba(0,0,0,.125);
    box-shadow:        inset 0px 1px 3px rgba(0,0,0,.125);
   -webkit-border-radius: 2px  !important;
   -moz-border-radius:    2px  !important;
    border-radius:        2px  !important;
  }
  .screenshot-thumbnail {
    max-height: none   !important;
    overflow-y: auto   !important;
  }
  .screenshot-caption,
  .screenshot-caption + br {
    display:  none;
  }
x .multiple-screenshot,
x .single-screenshot {
    max-width:  640px  !important;
  }
  .screenshot {
    background: #fff;
    max-width:  334px  !important;
    padding:    2px;
    margin:     10px;
    border:     solid 1px #fff  !important;

    border-color: rgba(0,0,0,.125)  !important;
   -webkit-box-shadow:  0px 1px 5px rgba(0,0,0,.25);
   -moz-box-shadow:     0px 1px 5px rgba(0,0,0,.25);
    box-shadow:         0px 1px 5px rgba(0,0,0,.25);
   -webkit-border-radius: 0px;
   -moz-border-radius:    0px;
    border-radius:        0px;
  }

  #more-screenshots {
    padding:  0 10px 5px;
  }

  
  #style-list li::after {
    content:      '+' attr(weekly-install-count) ' (' attr(total-install-count) ') / ' attr(average-rating);
    line-height:  1;
    font-size:    80%;
    margin:       0ex 0 0 1ex;
  }
  #style-list li.no-rating::after {
    content:      '+' attr(weekly-install-count) ' (' attr(total-install-count) ')';
  }
  #style-list li.obsolete::after {
    text-decoration:  none  !important;
  }

  
  fieldset,
  #obsoletion-message,
  #install {
    background: hsla(120, 30%, 85%, 1) !important;
    font-size:  90%            !important;
    padding:    10px 10px 0    !important;
    margin:     0 0 20px 20px  !important;
    width:      220px          !important;
    border:     4px solid #fff !important;

   -webkit-border-radius: 0  !important;
   -moz-border-radius:    0  !important;
    border-radius:        0  !important;
   -webkit-box-shadow:  0px 2px 8px rgba(0,0,0,.25);
   -moz-box-shadow:     0px 2px 8px rgba(0,0,0,.25);
    box-shadow:         0px 2px 8px rgba(0,0,0,.25);
  }
  #install > span {
    display:  block;
    margin:   10px 0;
  }
  #obsoletion-message {
    background: #eec     !important;
    padding:    0 10px 0 !important;
  }
  #generic-obsoletion-message {
    font-variant: small-caps !important;
    font-size:    115%       !important;
  }
  #specific-obsoletion-message {
    font-style: italic  !important;
  }

  #style-options {
    padding-bottom: 1ex  !important;
  }
  #style-options h3 {
    margin-bottom:  1ex  !important;
  }
  fieldset {
    padding:    1ex 3ex 1ex 2ex !important;
    margin:     0 2ex 10px 0    !important;
    width:      auto  !important;
    min-width:  15ex;
    vertical-align:   top;
    border-top-width: 3ex  !important;
  }
  legend {
    line-height:  3ex   !important;
    background:   #fff  !important;
    padding:      0 1ex !important;
    margin:       0;
  }
}
