css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Yahoo! Japan Translation
============================================ */
@-moz-document url(http://honyaku.yahoo.co.jp/), url(http://honyaku.yahoo.co.jp/transtext) {

html {background:#D3D3D3; border-top:36px solid #4F4F4F;}
body {border-top:1px solid #EEE; margin:0 !important;}
td {background:none !important;}

form table[width="90%"][cellspacing="0"][cellpadding="0"][border="0"] {width:100% !important;}
td small a[href$="/settings"] {position:absolute; top:10px; right:20px; background:#777; color:#CCC !important; padding:1px 12px 1px 12px; -moz-border-radius:8px;}

body div p table[width="675"][cellspacing="0"][cellpadding="0"][border="0"] {width:944px !important; margin-top:-18px;}
form table tbody tr td[width="280"][valign="top"]{width:422px !important;}

textarea.smaller {height:342px !important; width:422px !important;
font-size:12px !important; line-height:18px !important; padding:1px; -moz-appearance:none;
background:-moz-linear-gradient(center top , #E9E9E9, #F7F7F7 40px) !important;
border:1px solid #FFF !important; -moz-border-radius:4px; -moz-box-shadow: 0 0 2px #666;}
textarea.smaller:focus {-moz-box-shadow: 0 0 5px #444;}

body div p table tbody tr td p table tbody tr td form table tbody tr td small {color:#D3D3D3;}
label[for="radio-CR-EJ"],label[for="radio-CR-JE"],label[for="radio-CR-CJ"],label[for="radio-CR-JC"],label[for="radio-CR-KJ"],label[for="radio-CR-JK"]
{font-size:13px !important; line-height:24px; color:#444 !important;}

form table tbody tr td[align="center"][width="1%"]{width:100px !important;}
input[type="submit"] {height:45px; width:120px;}
input[type="button"] {height:45px; width:55px;}
img[src$="/ya.gif"] {position:absolute; top:5px; left:-8px; width:0 !important; height:0 !important; padding:0 0 27px 127px !important;
background:url(data:image/png;base64,)no-repeat !important;}

/* Hidden Stuff */
img[src$="/tab_set_off.gif"],
img[src$="/tab_r_off_02.gif"],
img[src$="/tab_r_off.gif"],
img[src$="/tab_l_on.gif"],
img[src$="/clear.gif"],
td small a[href$="/search"],
td small a[href$="/url"],
div[id="wrapper"],
td[width="75%"],
table[width="100%"][cellspacing="0"][cellpadding="6"][border="0"],
table[cellpadding="1"],
font[style="font-family: MS UI Gothic,Osaka,sans-serif; font-size: 10pt;"],
td > center,
tr > td > small > b,
hr[width="675"],
html > body > div > p > table > tbody > tr > td > p > table > tbody > tr > td > p,
table[border="0" ][width="100%"][cellspacing="1"][cellpadding="5"],
.box_info, #uftMain, #uhdMain
{display:none !important;}
}


/* excite Translation
============================================ */
@-moz-document url-prefix(http://www.excite.co.jp/world/) {

a, a:active {text-decoration:none !important;}
body, #wrapper {background:#D3D3D3 !important;}
.mTop10 {margin-top:14px !important;}
div.taFrame {height:342px !important; width:422px !important; padding:8px !important;}
textarea#before, textarea#after, textarea#reverse {
font-size:12px !important; line-height:18px !important; height:342px !important; width:422px !important; padding:1px !important; -moz-appearance:none;
background:-moz-linear-gradient(center top , #E9E9E9, #F7F7F7 40px) !important;
border:1px solid #FFF !important; -moz-border-radius:4px; -moz-box-shadow: 0 0 2px #666;}

textarea#before:focus, textarea#after:focus, textarea#reverse:focus {-moz-box-shadow: 0 0 5px #444 !important;}
textarea#reverse {position:absolute; left:546px; top:450px; height:186px !important;}

table#transTable tr td.spaceCol1, table#transTable tr td.spaceCol2,
table#transTable tr td.spaceCol3, table#transTable tr td.spaceCol4 {width:100px !important;}
table#transTable tr td.spaceCol4 {vertical-align:top !important; padding-top:20px !important;}

select#swb_lp {color:#333 !important; font-size:14px !important; width:85px !important; text-align:center !important;}
table#transTextButton tr td {background:none !important;}
input#t-submit {width:85px !important; height:45px !important;}
a#trans-clear {color:#555 !important;}

/* Hidden Stuff */
div[class^="ad"], #control, #nav, .adText, .boxes, #HS-table, .tieup, #underNav, #footer, iframe[width="300"],
td.spaceCol3 img, #excite_slidead_wrap, #external_flash_ad, #adAfter, #adMover, div.taTab
{display: none !important;}
}
