css:
@-moz-document 
url-prefix(http://stackoverflow.com) {

/**
*  Global
*    - Gloabal font size set, reduced
*    - Background for top nav and rounded corners
*    - Removed SE logo and shifted main nav down
*
**/

body {
  font-size: 11px !important;
}

h3 {
  font-size: 145% !important;
}

#tabs a {
  font-size: 96% !important;
}

#tabs a.youarehere {
  font-size: 120% !important;
}

#wmd-input {
  font-size: 12px !important;
}

#topbar {
  background-color: #eee !important;
  border-radius: 0 0 4px 4px;
  -moz-border-radius: 0 0 4px 4px;
  -webkit-border-radius: 0 0 4px 4px;
  border: 1px solid #ddd !important;
  border-top-width: 0px !important;
}

.container {
  background: 0 !important;
}

#hlogo, #hmenus {
  margin-top: -4px !important;
}

#header {
  height: 115px !important;
}

#custom-header {
  display: none !important;
}

/**
*  Question lists
*    - Adds padding to the left of questions to ensure when 
*      highlighted there would be some gap there
*    - Remove top whitepsace gap from list of question
*    - Bounty indicators moved to the right
*    - Better (I think, still needs work) color for highlighted votes box
*    - Fix for words getting push to right in rare cases by tags list
*
**/

.messages {
  width: 540px !important;
}

.narrow .votes {
  margin-left: 10px !important;
}

#question-mini-list, #questions {
  margin-top: -15px !important;
}

.question-summary {
  padding-bottom: 13px !important;
  width: 735px !important;
}

div[id^=suggested-edit].question-summary {
  width: 960px !important;
}

.narrow.tagged-interesting .votes {
  border: 3px solid #FFD100 !important;
  padding: 2px !important;
}

.bounty-indicator {
  float: right !important;
  margin-top: 2px !important;
  padding: 1px 4px 3px 3px !important;
}

.excerpt .tags {
  float: none !important;
}

.statscontainer .views {
  font-weight: bold !important;
}

.reputation-score {
  font-size: 110% !important;
}

/**
*  Sidebar
*    - Sidebar headers styles
*    - Moved up 'all tags' and 'all badges' link
*
**/

#sidebar {
  margin-top: 12px !important;
}

.module h4 {
  border-bottom: 1px solid #999 !important;
  margin-bottom: 8px !important;
  padding-bottom: 2px !important;
}

/**
*  Badges
*    - Reduced border-radius
*    - Tightened badge size
*
**/

.badge, .badge-tag {
  -moz-border-radius: 3px !important;
  -webkit-border-radius: 3px !important;
  border-radius: 3px !important;
  padding: 2px 6px 3px 3px !important;
  display: inline !important;
}

.user-stats-table .badge + br, 
.user-stats-table .badge + span + br, 
.user-stats-table .badge-tag + br, 
.user-stats-table .badge-tag + span + br {
  margin-bottom: 10px !important;
}

.badge1, .badge2, .badge3 {
  vertical-align: -4px !important;
}

#recent-badges td {
  height: 26px !important;
}


/**
*  Tags
*    - Changed sidebar interesting and ignored tag 
*      removal button position
*    - Removed sponsored tag icons
*    - Two column for tag stats
*
**/


#interestingTags > a, #ignoredTags > a {
  font-size: 96% !important;
  padding-left: 6px !important;
  padding-right: 6px !important;
}

.sponsor-tag-img {
  display: none !important
}

#recent-tags-list {
  column-count: 2 !important;
  -moz-column-count: 2 !important;
  -webkit-column-count: 2 !important;
}

#recent-tags-list a {
  white-space: normal !important;
  line-height: 14px !important;
}

#recent-tags-list br {
  margin-bottom: 12px !important;
}

/**
*  Question List Tabs
*    - Changed Featured Question Count Display
*
**/

.bounty-indicator-tab {
  float: left !important;
  line-height: 1em !important;
  margin-top: 3px !important;
  padding: 3px 4px 4px !important;
}

a:hover .bounty-indicator-tab {
  margin-top: 2px !important;
}

.youarehere .bounty-indicator-tab {
  font-size: 10px !important;
  padding: 4px 6px 5px !important;
  margin-top: 4px !important;
}

a.youarehere:hover .bounty-indicator-tab {
  margin-top: 4px !important;
}

/**
*  Add Interesting and Ignored Tags
*    - Reduced input box and button height
*    - Hover styles for button and input
*
**/


#interestingTags > a, #ignoredTags > a {
  padding-right: 18px !important;
  font-size: 96% !important;
}


#interestingTags > a + span, #ignoredTags > a + span {
  background-position: -2px -692px !important;
  width: 10px !important;
  height: 10px !important;
  margin-left: -17px !important;
  margin-right: 10px !important;
  margin-top: 7px !important;
  opacity: 0.4 !important;
}

#interestingTags > a + span:hover, #ignoredTags > a + span:hover {
  background-position: -2px -667px !important;
  opacity: 1 !important;
}


#interestingTag, #ignoredTag {
  height: auto !important;
  width: 160px !important;
}

#interestingAdd, #ignoredAdd {
  height: 22px !important;
  padding: 0 6px 2px !important;
  cursor: pointer !important;
  margin-left: -1px !important;
  font-size: 12px !important;

  -moz-border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
  border-radius: 0 !important;

  -moz-box-shadow: none !important;
  -webkit-box-shadow: none !important;
  box-shadow: none !important;
}

#ignoredTags, #interestingTags {
  margin-bottom: 10px !important;
}

#interesting-tags input[type='text'] {
  opacity: 0.7 !important;
}


#interesting-tags input[type='text']:hover, 
#interesting-tags input[type='text']:focus{
  opacity: 1 !important;
}

/**
*  Tag Stats
*    - Fixed bug caused by Jon Skeet answering 
*      too many questions :D
*    - Moved tag stats to display horizontally
*
**/

#questions > h2 + table {
  margin: 15px 0 30px !important;
}

#questions > h2 + table tr {
  float: left !important;
  margin-right: 20px !important;
}

#questions > h2 + table tr td + td {
  padding-left: 5px !important;
}

/**
*  Answer Editor
*    - Change width of components to match width of elements above it
*
**/

.answer + #post-form #post-editor, .answer + #post-form #wmd-input, 
.answer + #post-form #wmd-preview {
  width: 710px !important;
}

.answer + #post-form #wmd-button-bar {
  width: 720px !important;
}

/**
*  Question
*    - Changed bounty question status to display in one line
*    - Remove the share this question link
*
**/

.question-status.bounty {
  padding-left: 15px !important;
  width: auto !important;
}

.question-status.bounty h2 {
  font-size: 16px !important;
  text-align: center !important;
}

.question-status.bounty h2 .bountry-award {
  display: inline-block !important;
  padding: 1px 4px 1px 3px !important;
}

#answers-header + h2.bottom-notice {
  display: none;
}

/**
*  Misc. Bugfixes
*    - Fixed 'Pollyana Effect' by cruelly cutting off 
*      excessively long spam comments :D
*    - Anchor in inline code display bug fixed with underline
*
**/

.comments {
  overflow: hidden !important;
}

code a {
  text-decoration: underline !important;
}

#favorites-table .question-summary {
  width: 900px !important;
}

input[type='button'], input[type='submit'], button {
    -moz-border-radius: 4px;
    -webkit-border-radius: 4px;
    border-radius: 4px;

    -moz-box-shadow: 0 1px 0px rgba(0, 0, 0, 0.4);

    background: -moz-linear-gradient(top, #f5f5f5, #d9d9d9) !important;
    font-size: 1.1em !important;
    padding: 5px 9px 6px !important;
    text-shadow: 0 1px 0 #FFFFFF;
    color: #333 !important;
}

input[type='button']:hover, input[type='submit']:hover, button:hover {
    background: -moz-linear-gradient(top, #f9f9f9, #efefef) !important;
    border-color: #999 !important;
    color: #333 !important;
}

/**
* 10k Tools area
*   - God are these HTML *ugly* 
*   - Cleans up a bunch of visual glitches and makes things 
*     slightly prettier. Sort of like sticking lipsticks on a pig
*
**/

.tools-page h2 + h4 {
  margin-top: -4px !important;
  margin-bottom: 6px !important;
  font-size: 13px !important;
  color: #999 !important;
}

.tools-page .header-row {
  padding: 6px 0 3px !important;
  font-style: normal !important;
  font-weight: bold !important;
  color: #666 !important;
}

.tools-page .module .bounty-indicator-tab {
  margin: 0 4px 0 0 !important;
  font-size: 12px !important;
}

.tools-page .comments .flagcount {
  margin: -1px 3px 0 0 !important;
}

.tools-page .answer-link {
  font-size: 13px !important;
}

.tools-page .post-summary {
  line-height: 1.22em  !important;
  font-size: 11px !important;
}

.tools-page .post-options {
  margin-left: 22px !important; 
}

.tools-page .dismiss-options {
  font-size: 13px !important;
  margin-top: -18px !important;
}

.tools-page a.cancel-comment-flag {
  color: #B20000 !important;
}

.tools-page #mainbar-full ul {
  -moz-column-count: 3;
}

.tools-page #mainbar-full ul li {
  padding: 0 !important;
  list-style: none !important;
}

.tools-page #mainbar-full ul li a {
  font-size: 13px !important;
  padding: 4px 15px 5px !important;
  display: block;
}

.tools-page #mainbar-full ul li a:hover {
  background: #efefef !important;
  text-decoration: none !important;
}

.tools-page .subtabs {
  width: 450px !important;
}

.tools-page #auto-load-newTags {
  position: relative;
  width: 960px !important;
}

.tools-page td.tag-col {
  display: block; 
  float: left;
  width: 150px !important;
}

/*
*
*/


.tools-page .island div:not(#auto-load-newTags) tr {
  width: 100%;
}

.tools-page .island div:not(#auto-load-newTags) tr a:hover {
  text-decoration: none !important;
}

.tools-page .island div:not(#auto-load-newTags) tr a {
  display: block !important;
  position: relative !important;
  padding-bottom: 6px !important;
  padding-left: 38px !important;
  margin-left: -38px !important;
}

.tools-page .island div:not(#auto-load-newTags) tr:hover {
  background: #f3f3f3 !important;
}

.tools-page #mainbar-full .island div[id^="auto-load-recently"] tr a {
  padding-left: 110px !important;
  margin-left: -110px !important;
}

.tools-page #mainbar-full .island div[id^="auto-load-recently"] tr td:first-child {
  padding-left: 10px !important;
  width: 100px !important;
}

.tools-page .island div:not(#auto-load-newTags) tr td:nth-child(2) {
  display: block;
}

/**
 *  .by-vote-count
 *
 *
**/

.tools-page .close-reason {
  display: block;
  text-align: right;
  text-transform: uppercase;
  font-size: 9px !important;
  font-weight: bold;
  margin-top: -20px !important;
  margin-bottom: 10px !important;
}

.tools-page .island tr.by-vote-count {
  border-bottom: 1px solid #eee !important;
}

.tools-page #mainbar-full .island tr.by-vote-count a {
  font-size: 13px !important;
  padding: 6px 0 20px 45px !important;
  margin-left: -45px !important;
}

.tools-page #mainbar-full .island .by-vote-count td {
  padding-top: 0 !important;
  padding-bottom: 0 !important;
}

.tools-page .island .by-vote-count td:nth-child(2) {
  padding-right: 10px !important;
}

.tools-page .island .by-vote-count .cnt {
  font-weight: bold;
  font-size: 13px !important;
}

/*
 * .with-score
 *
 */

.tools-page #mainbar-full .island .with-score a {
  position: relative;
  display: block !important;
  padding: 4px 0 4px 48px !important;
  margin-left: -48px !important;
}

/**
* Review Pages
*   - Increase font-size on answers title
*   - Fixed font for Markdown diff
*
**/

.review-page .answer-link, 
.review-page .edit-suggestion .question-hyperlink {
  font-size: 14px !important;
  margin: 7px 0 5px !important;
}

.review-page .module.newuser {
  margin-bottom: 25px !important;
}

.review-page .subtabs.filters {
  margin-top: 20px !important;
}

.edit-suggestion .body-diffs .full-diff td.content {
  font-family: Consolas,Menlo,Monaco,Lucida Console,Liberation Mono,DejaVu Sans Mono,Bitstream Vera Sans Mono,Courier New,monospace !important;
  line-height: 1.1em !important;
  padding: 4px 10px !important;
  max-width: 415px !important;
  color: #666;
}

.popup-actions .bounty-indicator-tab {
  margin-top: 12px !important;
}

}
