css:
#nav-bar {
margin: -3px 0 -6px 0px !important;
background-image: -moz-linear-gradient(bottom,#f7f7f7, #f9f9f9 100%) !important;
}

menubar {
background: #f9f9f9 !important;
}

#PersonalToolbar {
background: #f7f7f7 !important;
}

radio[pane]:hover,
#viewGroup radio:hover {
color: white !important;
background-image: -moz-radial-gradient(center 45deg, circle closest-corner, rgba(255,255,255,1) -100%, rgba(10,140,140,1)125%) !important;
}

radio[pane][selected="true"],
#viewGroup radio[selected="true"] {
color: white !important;
background-image: -moz-radial-gradient(center 45deg, circle closest-corner, rgba(255,255,255,1) -100%, rgba(10,140,140,1)125%) !important;
}

richlistitem[selected="true"] {
color: white !important;
background-color: #666 !important;
}

tabs {
height: 20px !important;
}

.tabbrowser-tab {
margin-right: -3px !important;
}

.tabbrowser-tab:first-child {
margin-left: 0 !important;
}

.tabbrowser-tab > .tab-text, .tabbrowser-tab > .tab-icon-image {
margin-top: -5px !important;
}

.tabbrowser-tab .tab-close-button {
margin-bottom: 4px !important;
}

.tabs-newtab-button {
border: 1px solid #cfcfcf !important;
margin: 0 0 4px 4px !important;
-moz-border-radius-topleft: 2px !important;
-moz-border-radius-topright: 2px !important;
padding: 0 2px !important;
background-color: #e6e6e6 !important;
}

