css:
@-moz-document url-prefix(http://www.bingdi.com/html/book/){

#TextSelect, #Menu,
#TextSelect+table,
#LinkMenu~table,#TopLine3,
#End, #Ws~*,
#LinkMenu td.tbsep,
#EndMenu td.tbsep,
#EndMenu table td:nth-of-type(2),
#EndMenu table td:nth-of-type(7),
#EndMenu table td:nth-of-type(15),
#EndMenu table td:nth-of-type(16),
#BookText br:nth-of-type(2n),
#BookText br:nth-last-of-type(2),
#BookText br:first-of-type,
#BookText br:last-of-type {
    display:none !important;
}

#Menu tr:nth-of-type(2) td:first-child {
    visibility: hidden !important;
}

body {
    background-color: #CCCCFF !important;
}

#Content {
    margin: auto !important;
    border: 10px solid #339999 !important;
    -moz-border-radius: 0px 25px 0px 25px !important;
    padding: 10px 40px 60px 40px !important;
    width: 75% !important;
}

#TextTitle {
    float: left !important;
    margin-bottom: 10px !important;
}

#BookText {
    clear: left !important;
    width: auto !important;
    margin: 0px !important;
    line-height: 24px !important;
}

.newstitle {
    border-bottom: 1px solid #339999 !important;
    text-align: left !important;
    width: 850px !important;
    padding-bottom: 10px !important;
    font-size: 16px !important;
    color: #222 !important;
}

#EndMenu table {
    width: 300px !important;
}

#EndMenu {
    float: left !important;
    margin: 5px 0px 0px 20px !important;
    padding-left: 20px !important;
}

#LinkMenu {
    float: right !important;
    margin: 5px 20px 0px 0px !important;
    padding-right: 20px !important;
}

#EndMenu table,
#EndMenu table a,
#LinkMenu table,
#LinkMenu table a {
    background: -moz-linear-gradient(center top, #339999 1px, #338899 10%, #337766) !important;
    color: #d8d8d8 !important;
    padding: 8px !important;
    -moz-border-radius: 10px !important;
    text-decoration: none !important;
}

#EndMenu table td:nth-of-type(9) a font {
    color: #d8d8d8 !important;
}

#LinkMenu td.tb0,
#EndMenu td.tb0 {
    border: none !important;
}

#LinkMenu table a:hover,
#EndMenu table a:hover {
    background: -moz-linear-gradient(center top, #339977 1px, #339988 10%, #339999) !important;
}
}
