css:
 #gc-header * { display: none; }

#gc-content {
	background: white;
	margin: 0px auto;
} 
.goog-splitpane-handle { display: none; }
 
.goog-splitpane-first-container {
	background: #D5D9E2;
	-webkit-border-radius: 0px 0px 5px 0px; }
 
.gc-inbox-sidebar-menu { background: #D5D9E2; }
 
.goog-splitpane { background: white; }
 
#gc-view-header {
	background: -webkit-gradient(linear, left top, left bottom, from(#cfcfcf), to(#a7a7a7));
	border-bottom: 1px solid grey;
	padding: 10px;
	-webkit-border-radius: 0px 5px 0px 0px; }
 
#gc-inbox-sidebar-header {
	background: -webkit-gradient(linear, left top, left bottom, from(#cfcfcf), to(#a7a7a7));
	border-bottom: 1px solid grey;
	padding: 15px;
	-webkit-border-radius: 5px 0px 0px 0px; }
 
#gc-splitpane { -webkit-border-radius: 5px; }
 
#gc-sidebar-balance , #gc-sidebar-invites { display: none; }
 
.goog-option-selected { background: -webkit-gradient(linear, left top, left bottom, from(#568CCC), to(#2761B5)); }
 
.goog-button-base-top-shadow { display: none; }
 
.goog-inline-block { background: rgba(255,255,255,0.0);}
 
.goog-button-base-pos {
	background: -webkit-gradient(linear, left top, left bottom, from(#fefefe), to(#b8b8b8));
	border: 1px solid #505050;
	border-left: 0px !important
	border-right: 0px !important;
        -webkit-box-shadow:0px 1px 0px rgba(255,255,255,0.5);
	margin-right: 0px;
        -webkit-border-radius:2px; }
 
#gc-inbox-refresh { text-shadow: 0px 1px 1px white; }
 
