css:
@namespace "http://www.w3.org/1999/xhtml";

@-moz-document url-prefix("http://tawerna.rpg.pl/forum"),
url-prefix("http://www.tawerna.rpg.pl/forum"),
url-prefix("http://trpg.pl/forum"),
url-prefix("http://www.trpg.pl/forum")  {

body{
  font-size: 11px !important;
  font-family: "Verdana" !important;
  background-color: #FCFAE6 !important;
  background-image: url(data:image/jpeg;base64,) !important;}

#wrapcentre {
  margin: 5px 25px 0 25px !important;}

#logodesc {
  background: none !important;
  border-bottom: none !important;
  font-family: "Monotype Corsiva", Batang !important;
  color: #382513 !important;}

#logodesc td {
  line-height: 25px !important;}

#logodesc strong {
   font-size: 20px !important;
  font-weight: normal !important;}

#logodesc a {
  color: inherit !important;}

#logodesc img {
  max-height: 110px !important;
  max-width: 320px !important;}

h1 {
  color: #382513 !important;
  font: italic normal normal 76px "Monotype Corsiva", Batang !important;
  min-width: 300px !important;
  line-height: 60px !important;}

h2 a {
  color: #0D004C !important;}

h4 a {
  color: #002F2F !important;}

th {
  color: #DADADA !important;
  background-color: #002F2F !important;
  background-image: none !important;
  -moz-border-radius: 5px 5px 5px 5px !important;}

th a {
  color: #FFA34F !important;}

.cat, .catdiv {
  font-size: inherit !important;
  height: 20px !important;
  background: transparent !important;}

.tablebg {
  background-color: transparent !important;}

.row1 {
  background-color: transparent !important;
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAAF0lEQVR4XgWAMQ0AAACCjG5aQzAOF1kPCS8DXbvzavUAAAAASUVORK5CYII=) !important;}

.row2 {
  background-color: transparent !important;
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAAF0lEQVR4XgWAgQwAAADCQk8jpJ+qBdcDBzkCrdbIMecAAAAASUVORK5CYII=) !important;}

.row3 {
  background: transparent !important;}

.gensmall>strong,
.profile>strong  {
  font-weight: bold !important;
  color: black !important;}

#menubar .genmed a,
#ucp .genmed a,
p.searchbar,
p.searchbar a,
.gensmall a,
.nav,
.nav>td>a,
.nav>b>a,
.nav>a,
.profile>strong>a {
  color: #444444 !important;}

.cat a.nav {
  font-weight: bold !important;
  color: darkred !important;}

p.linkmcp a {
  color: darkgreen !important;}

p.breadcrumbs a  {
  color: #0D004C !important;}

.quotetitle, .attachtitle {
  -moz-border-radius: 5px 5px 0px 0px !important;
  background: #002F2F !important;
  border: 1px solid #002F2F !important;
  color: #F6F5E5 !important;}

.quotecontent, .attachcontent {
  background: #F6F5E5 !important;
  border: 1px dotted #002F2F !important;
  color: #002F2F !important;}

.codetitle {
  -moz-border-radius: 5px 5px 0px 0px !important;
  background: #0D004C !important;
  border: 1px solid #0D004C !important;
  color: #F6F5E5 !important;}

.codecontent {
  background: #F6F5E5 !important;
  border: 1px dotted darkblue !important;
  color: #0D004C !important;}

a {
  color: #493F0B !important;}

a[style="color: rgb(170, 0, 0);"] {
  color: rgb(170, 0, 0) !important;}

a[style="color: rgb(0, 0, 102);"] {
  color: rgb(0, 0, 102) !important;}

a[style="color: rgb(0, 102, 0);"] {
  color: rgb(0, 102, 0) !important;}

a[style="color: rgb(0, 0, 255);"] {
  color: rgb(0, 0, 255) !important;}

a[style="color: rgb(75, 0, 130);"] {
  color: rgb(75, 0, 130) !important;}

a[style="color: rgb(204, 255, 204);"] {
  color: rgb(204, 255, 204) !important;}

.forumlink {
  color: #0D004C !important;
  font-size: 13px !important;}

#ucp a.gen,
.topictitle,
.topictitle a {
  color: #002F2F !important;
  font-weight: bolder !important;}

.postlink {
  color: #467865 !important;
  font-style: italic !important;} 

.pm_marked_colour {
	background: #000000 !important;}
.pm_replied_colour {
	background: #A9B8C2 !important;}
.pm_friend_colour {
	background: #007700 !important;}
.pm_foe_colour {
	background: #DD0000 !important;}

.spacer {
  background-color: transparent !important;
  border-top: 1px dotted black !important;
  border-bottom: 1px dotted black !important;}

.post,
textarea,
select,
option,
.btnbbcode,
.btnmain,
.btnlite,
.helpline {
  background: #F6F5E5 !important;}
}
