css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.3zai.com") {

/* clear table layout */
form[id^="account_modify_form"] {
  padding-top:8px !important;
}
form[id^="account_"] table.form-list,
form[id^="account_"] table.form-list tr:not(.head),
form[id^="account_"] table.form-list tr:not(.head)>td {
  display:block !important;
  background-image:none !important;
  margin:0 !important;
  padding:0 !important;
}
form[id^="account_"] table.form-list tr:not(.head)>th {
  display:none !important;
}
form[id^="account_"] div.inner-section fieldset {
  position:relative !important;
}
form[id^="account_"] input,
form[id^="account_"] select {
  margin:0 !important;
}

form[id^="account_"] p.attention {
  display:none !important;
}

/* common fieldset */
form[id^="account_"] div.inner-section {
  min-height:65px !important;
  margin-bottom:10px !important;
}
form[id^="account_"] table.form-list tr.head {
  height:28px !important;
}
form[id^="account_"] table.form-list tr.head th,
form[id^="account_"] table.form-list tr.head td {
  padding:4px 10px !important;
}
form[id^="account_"] table.form-list tr.head div[id^="close_item"] {
  display:inline !important;
}
form[id^="account_"] div.inner-section ul.button-function {
  margin-top:15px !important;
}
#item_add {
  margin:-50px 10px 0 !important;
  padding-top:50px !important;
}

/* common name field */
form[id^="account_"] div.inner-section input[name*="_name"] {
  position:absolute !important;
  top:0 !important;
  width:300px !important;
}
form[id^="account_"] div.inner-section input[name*="_name"]+div.cmDiv {
  position:absolute !important;
  top:17px !important;
  left:0 !important;
}
form[id^="account_"] div.inner-section input[name*="_name"]+div.cmDiv span.arrow-block {
  position:absolute !important;
  top:-13px !important;
  left:290px !important;
  width:9px !important;
  height:9px !important;
}
form[id^="account_"] div.inner-section input[name*="_name"]+div.cmDiv span.arrow-block>a {
  display:none !important;
}
.clickMenu div.outerbox {
  z-index:1000 !important;
}

/* shop name field */
form[id^="account_"] div.inner-section input[name^="shop_name"] {
  top:1px !important;
}
form[id^="account_"] div.inner-section input[name^="shop_name"]+div.cmDiv {
  top:18px !important;
}

/* shop time field */
form[id^="account_"] select[name^="timespan_seq"] {
  position:absolute !important;
  top:0 !important;
  left:305px !important;
  width:80px !important;
}

/* shop place field */
form[id^="account_"] div.inner-section tbody>tr:not(.head):first-child+tr+tr td {
  position:absolute !important;
  top:21px !important;
  left:0 !important;
  white-space:nowrap !important;
}

/* shop place field */
form[id^="account_"] div.inner-section tbody>tr:not(.head):first-child+tr+tr+tr td {
  position:absolute !important;
  top:21px !important;
  left:230px !important;
  white-space:nowrap !important;
}

/* item alert */
form[id^="account_"] div.inner-section p.alert {
  position:absolute !important;
  top:0 !important;
  left:315px !important;
  width:250px !important;
  height:15px !important;
  padding:3px 5px 3px 20px !important;
  background-position:5px 5px !important;
}

/* item name field */
form[id^="account_"] div.inner-section input[name^="item_name"] {
  top:30px !important;
}
form[id^="account_"] div.inner-section input[name^="item_name"]+div.cmDiv {
  top:47px !important;
}

/* item unit price & amount field */
form[id^="account_"] div.inner-section tr.head+tr+tr td {
  position:absolute !important;
  top:30px !important;
  left:305px !important;
  white-space:nowrap !important;
}
form[id^="account_"] input[name^="unit_price"] {
  width:52px !important;
  text-align:right !important;
}
form[id^="account_"] input[name^="amount"] {
  width:18px !important;
  text-align:right !important;
}
form[id^="account_"] div.inner-section tr.head+tr+tr td p.alert {
  top:-30px !important;
  left:0 !important;
}

/* item genre field */
form[id^="account_"] div.inner-section tr.head+tr+tr+tr td {
  position:absolute !important;
  top:29px !important;
  left:435px !important;
  white-space:nowrap !important;
}
form[id^="account_"] select[name^="account_genre_seq"],
form[id^="account_"] select[name^="account_category_seq"] {
  width:80px !important;
}

/* item memo field */
form[id^="account_"] textarea[name^="memo"] {
  position:absolute !important;
  top:50px !important;
  left:0 !important;
  width:300px !important;
  height:15px !important;
}
form[id^="account_"] textarea[name^="memo"]:focus {
  height:100px !important;
  z-index:1000 !important;
}
form[id^="account_"] div.inner-section tr.head+tr+tr+tr+tr td p:nth-child(2) {
  position:absolute !important;
  top:50px !important;
  left:310px !important;
  white-space:nowrap !important;
}

/* item image field */
form[id^="account_"] div.inner-section tr.head+tr+tr+tr+tr+tr {
  display:none !important;
}

/* item check field */
form[id^="account_"] div.inner-section table.form-list+div.check-item {
  _color:transparent !important;
  _display:block !important;
  position:absolute !important;
  overflow:hidden !important;
  top:44px !important;
  left:475px !important;
  width:102px !important;
  padding-left:16px !important;
  white-space:nowrap !important;
}
form[id^="account_"] div.inner-section input[name^="open_flag"] {
  position:absolute !important;
  top:6px !important;
  left:0 !important;
}

}
