@-moz-document domain(slashdot.org) {

#advertisement-content { display: none; }

#advertisement-title { display: none; }

.ad1, .ad2, .ad3, .ad4, .ad5, .ad6, .ad7, .ad8, .ad9, .ad10 { display: none; }

}

