css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("minichan.org") {
 body{
  background-color:#000 !important;
  color:#fff !important;
  font-family:arial, sans-serif;
  font-size:12px;
  padding:0 1em;
  margin:0px;
  }
  .body{
   background-color:#fff !important;
   color:#000 !important;
   line-height:1.5em;
   margin:0;
   overflow:hidden;
   padding:.5em;
   white-space:normal;
   word-wrap:break-word;
  }
  .body img{
   border:none;
   float:left;
   margin-bottom:6px;
   margin-right:7px;
  }
  .body ul.menu{
   clear:both;
   margin:.5em 0 0;
   text-align:right;
  }
   .body ul.menu li{
    margin:0 0 0 .5em;
   }

 .codebox{
         overflow: auto;
         display: block;
         height: auto;
         max-height: 250px;
         white-space: normal;
         padding-top: 5px;
         font: 0.9em monaco, "andale mono","courier new", courier, mono;
         line-height: 1.3em;
         color: #8b8b8b;
         margin: 3px 0;
 }

 span.quote{
  color:#f00 !important;
 }

 .highlighted{
  background-color:#63a15d;
 }

 .spoiler{
  background-color:#00f !important;
  color:transparent !important;
 }
  .spoiler:hover{
   background-color:#0ff !important;
   color:#000 !important;
  }

 .border{
  background-color:#f77 !important;
  border:3px dashed #ff0 !important;
  margin:1em;
  padding:.5em;
 }

 .highlight{
  background-color:#ff9;
  color:#f00 !important;
  font-style:normal;
  font-weight:700;
 }

 a{
  color:#070 !important;
 }

 #notice{
  background-color:#d8f8d4;
  border:2px solid #95d38e;
  padding:.5em;
 }

 h1{
  font-size:17px;
  text-align:right !important;
 }
  h1 a{
   color:#000;
   text-decoration:none;
  }

 h2{
  font-size:16px;
 }
  h2 a:hover{
  text-decoration:underline;
  }

 h3{
  background-color:#7f00ff !important;
  color:#ff0 !important;
  font-size:12px;
  font-weight:400;
  margin:0;
  padding:.5em;
 }

 .reply_id{
  float:right;
  text-align:right;
  vertical-align:middle;
 }

 h4.section{
  font-size:15px;
  margin-bottom:7px;
 }

 h4.user{
  font-size:14px;
  margin:0 0 .5em;
 }

 label.common{
  float:left;
  padding-right:.5em;
  text-align:right;
  width:15em;
 }

 input,textarea{
  display:block;
 }

 textarea{
  font-family:monospace;
  width:100%;
 }

 input[type="text"]:focus,input[type="password"]:focus,textarea:focus{
  outline:1px solid #eaffe8;
 }

 fieldset{
  margin:1em 0;
 }

 ul.body,ul.error_message{
  list-style-type:none;
 }

 ul.error_message{
  margin:0;
  padding:0;
 }
  ul.error_message li{
   font-style:italic;
   padding:.2em;
  }

 ul.menu{
  margin:1em;
  text-align:center;
 }
  ul.menu li{
   display:inline;
   font-weight:700;
   margin:0 1em;
  }

 ul#main_menu{
  background-color:#00f !important;
  margin:0 0 1em;
  padding:.5em;
 }

 ul.stuff{
  margin-bottom:2em;
 }
  ul.stuff li{
   margin-bottom:.5em;
  }

 table{
  border-collapse:collapse;
  margin-bottom:1em;
  width:100%;
 }
  table th{
   background-color:#00f !important;
   padding:.5em;
   text-align:left;
  }
  table td{
   background-color:#f00 !important;
   padding:.5em;
   vertical-align:top;
  }
  table tr.odd td{
   background-color:#0f0 !important;
  }
  table tr:hover td{
   background-color:#ff0 !important;
  }
  table .minimal{
   white-space:nowrap;
   width:1%;
 }

 .profile_fix{
         overflow:hidden;
         white-space:normal;
         word-wrap: break-word;
 }

 .reply_body_snippet,.snippet{
  font-style:italic;
 }

 .topic_headline a:visited{
  font-weight:400;
 }

 .topic_info{
  float: right;
  cursor: default;
 }

 em.marked{
  background-color:#ff9;
  font-style:normal;
  font-weight:700;
 }

 p.caption{
  font-size:11px;
  margin-top:2px;
 }

 .inline{
  display:inline;
 }

 #last_seen_marker{
  border-top:2px solid #c3eebe;
 }

 #snapback_link{
  left:2px;
  position:fixed;
  top:50%;
 }

 .video,video,a.video{
  clear:both;
  margin:10px 0;
 }

 .standalone,.row{
  margin-bottom:1em;
 }

 .body ul.menu li,.body ul.menu li a,.body ul.menu li span,.unimportant,.unimportant *{
  font-size:10px;
 }

 a:hover,h2 a,#snapback_link a{
  text-decoration:none;
 }

 a.youtube_alternate,.noscreen{
  display:none;
 }

 h3#preview,h3#error,h3#replying_to,label,legend,.topic_headline a,.reply_body_snippet a,.snippet a{
  font-weight:700;
 }

 .help,.help_cursor{
  cursor:help;
 }
}
