css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.poneyvallee.com/forum/") {

/*Images par Mathilde-Wendywee*/
body {
	background : #FFFFFF url("http://mathilde.feerik.com/themes/mathilde/images/fondpagegris.jpg") center fixed repeat !important;
	color : #444444 !important;
}

#brdmenu+div {
	background : none !important ;
}

img[src*="../images/accueil/pv_titre.png"] {
	background: url("http://nsm08.casimages.com/img/2014/10/16//14101601595210339312617108.png") no-repeat ;
	width : 0 ;
	height : 0 ;
	padding-top : 110px ;
	padding-left : 650px ;
}
	
#brdheader {
	background: white url("http://nsm08.casimages.com/img/2014/10/16//14101601595210339312617107.png") no-repeat right top !important;
	background-size : auto 150px !important;
	border : 2px #78A24C solid !important;
	margin-top:5px !important;
}


#brdmenu[style="background-color:#315B45;margin-left:5px;margin-right:5px"] {
	background: transparent !important;
}

#brdheader .box{
	background: transparent !important;
	border : none !important;
}
#brdmenu {
	background: transparent !important;
}
#brdmenu a{
	color : #BCDC99 !important;
	border-bottom: none !important;
}
#brdmenu a:hover{
	color : #66C29B !important;
	border-bottom: #6BA380 dotted 2px !important;
}
#brdwelcome li {
	color : #77A34C !important;
}
#brdwelcome li a{
	color : #9FB374 !important;
	border-bottom : 1px dotted #9CB073 !important;
}



h2 {
	background: linear-gradient(to left, #81B052, #97CA63, #77A34A, #82B04E, #BCDC99) repeat fixed !important;
	color : white !important ;
	border : 2px #78A24C solid !important;
	border-bottom : none !important ;
}

thead th, .arrondis_bas {
	background: white !important;

}
.arrondis_bas {
	border : 2px #78A24C solid !important;
}

tbody td, thead th {
	background: transparent !important;
	border : none !important ;
	border-top : 1px solid #BCDC99 !important ;
}
tbody h3 a {
	border-bottom : none !important ;
}


.tclcon {
	color : #69B992 !important ;
}
.tclcon a{
	color : #69A37D !important ;
}
.tclcon a:hover{
	border-bottom : #68BA92 1px dotted !important ;
}
.tclcon p a{
	border-bottom : #69A37D 1px solid !important ;
}

.tc2, .tc3 {
	color : #97CC62 !important ;
}

.tcr {
	color : #A8BB7B !important ;
}
.tcr a {
	color : #A8BB7B !important ;
	border-bottom : #A8BB7B 1px dotted !important ;
}
.tcr a:hover {
	color : #9BB173 !important ;
}

.conl, dd {
	background: transparent !important ;
}
#brdstats .box, #brdfooter .box {
	background: #BCDC99 url(http://mathilde.feerik.com/themes/mathilde/images/star.png) no-repeat center  !important;
	border : 2px #67A37D solid !important;
	color : white !important ;
}
#brdstats .box a, #brdfooter .box a{
	color : #6AC099 !important ;
	border-bottom : none !important;
}


.blocktable .box {
	background: rgb(255, 255, 255) !important;
	border : 2px #78A24C solid !important;
}


.isticky .tclcon a {
	border-bottom : 1px dashed #69A37D !important;
}


.box dl, .postright {
	background: transparent !important;
}
.postfootright {
	background: white !important;
}
.postleft, .postfootleft {
	background:  #BCDC99 !important;
}
.postmsg a {
	color : #71003B !important;
	border-bottom: 1px dotted #71003B !important;
}
.postmsg a:hover {
	color : black !important;
	border-bottom: 1px dotted black !important;
}
.usertitle {
	color : #77A34C !important;
}
.postleft dt a{
	color : black !important;
	border-bottom: none !important;
}
.usercontacts a{
	color : #503551 !important;
	border-bottom: 1px dashed #503551 !important;
}
.linkst a{
	color : white !important;
	border-bottom: 1px dashed white !important;
}

DIV.postright {
	BORDER-LEFT: solid 198px #BCDC99 !important;
}
.box, .blockpost {
	background : white none !important;
}
.blockpost .box {
	border : 2px #78A24C solid !important;
}
.blockpost a {
	color : black !important ;
	border-bottom : 2px dotted white !important ;
}


}
