/* Border Patrol */
PRE,
.commentHead,
.toccolours,
[class="messagebox standard-talk"]
 {
 color: #eee !important;
 border-spacing: 1px !important;
 border-top-color: #444 !important;
 border-left-color: #444 !important;
 border-bottom-color: #444 !important;
 border-right-color: #444 !important;
 background-color: #102 !important;
 }


.form-noident
 {
 border-color: #002 !important;
 }

/* All Links */
/* Active */
a,
a:active,
#gbar a
 {
 color: #5cf !important;
 }

/* Active Hover */
a:hover,
#gbar a:active
 {
 color: #5f5 !important;
 font-weight: bolder !important;
 text-decoration: underline !important;
 }

/* Personal Menu Links */
#p-personal li a:hover { background-color: #333 !important; }

/* BrighText */
[class="navbox collapsible"]
 {
 color: #fff !important;
 background-color: #102 !important;
 }


/*=============================================*/
/* Wikipedia */
[class="navselect"]
 {
 background-color: #005 !important;
 }

#wikibox img,

div.thumb,
div.thumb div a img
 {
