css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.camptocamp.org") {

/* based on Arch Linux Forums Style - original by Hrwa
   (http://userstyles.org/styles/13263)

colors {{{
  background: #141414 !important;

  background-color: #242424 !important;
  border-color: #343434 !important;

  background-color: #343434 !important

  border-color: #999999 !important;
  border-color: #0771a6 !important;

  h2 { background: #1a1a1a !important;}

  a {color: #608baa !important;} //color: #709bdd !important;
  strong a {color:#9fbc00 !important;}
  a:hover{color: #aaa !important;}
  header a {color: white !important;}

  textarea, input, select {
    border: 1px solid #343434 !important;
    color: white !important;
    background: #141414 !important;
  }
}}}
*/

html, body, #holder, .pun {
  min-width: 0 !important;
  font: 14px/1.5 "Droid Sans", sans-serif !important;
  background-color: #141414 !important;
  color: #aaa !important;
}

/* REMOVE STUFF */

#path, .ombre_haut, .ombre_bas, #nav_buttons, #wizard_button, #nav_space, #pub, #nav_anchor, .translate_button, #menu_border_left, #menu_border_right, #splitter, #footer_border_left, #cc, #footer_border_right, #footer_cc_text > form, .picto_open, .picto_close, .go_up, .nav_box_top, .nav_box_down, #nav_tools_top, #nav_tools_down, #nav_prepare, #nav_share, .switch_lang div:first-child, #menu_content ul ul ul, #nav_edit li a.action_comment span.reduced, #tip_data, #tip_map_container, #tip_description, #tip_images, #tip_outings, #tip_associated_docs, #tip_view, #_description, .action_toggle, .outings_content #map_container_section_container, #map_container .default_text, span.toc_link {
  display: none !important;
}

/* RESET */

.content_article, .content_article .article, #content_box, #punwrap > div, .pun, #wrapper_context, #article, #home_background_content {
  float: none !important;
  position: relative;
  background: transparent !important;
  border: 0 !important;
  margin: 0 !important;
  padding: 0 !important;
}

#home_left_content, #home_right_content {
  margin: 0 !important;
  padding: 0 !important;
  background: transparent !important;
}

/* GLOBAL LAYOUT */

#header, #menu, #content_box, #footer, #punwrap {
  margin: 0 auto !important;
  padding: 0 !important;
  width: 90% !important;
  min-width: 900px;
}

.content_article, #footer, #MB_window, #MB_frame, #edit_me, .nav_box, #punwrap {
  background-color: #242424 !important;
  background: #242424 !important;
  border: 1px solid #343434 !important;
}
.article_title_img { margin-left: 0 !important; }
.article_titre_bg, .home_title_text, .latest li, .articles_content .htext, #article.articles_content .htext {
  border-bottom: 2px solid #343434 !important;
}
.latest li { border-width: 1px !important; }
.clearing, #punviewtopic .clearing { padding: 25px 0 5px !important; }
.content_article, #punwrap > div, .pun { padding: 10px !important; width:98% !important; }

/* GLOBAL STYLES */

pre, code {
  font-family: "Droid Sans Mono", monospace !important;
  font-size: 0.95em !important;
}

q { quotes: '«' '»'; }
q:before { content: open-quote "\0000a0"; }
q:after { content: "\0000a0" close-quote; }

blockquote, div.codebox {
  margin: 1em !important;
  padding: 1em !important;
  border: 1px solid #343434 !important;
}
blockquote {
  font-style: italic !important;
}

input, select, textarea, .c2cui_btn {
  /* -moz-appearance: none !important; */
  border: 1px solid #343434 !important;
  color: white !important;
  background-color: #141414 !important;
}
input, textarea, .c2cui_btn {
  background: #141414 !important;
}
input:hover { background-color: #343434 !important; }
fieldset { border: 1px solid #343434 !important; }
legend { color: #aaa !important; }
option { background-color: #141414 !important; }

.img_box { border: 1px solid #999999 !important; }

.checkbox_list li label { background-color: #343434 !important; color: white !important; }
.checkbox_list li label:hover { background-color: #608baa !important; }
.checkbox_list li.checked label { background-color: #9fbc00 !important; }

/* TITLES */

.htext, .article .htext {
  margin: 0.5em 0 0;
  padding: 0 !important;
  font-family: "Droid serif", serif !important;
}
h4, h2.htext { font-size: 1.25em !important; }
h5, h3.htext { font-size: 1.1em !important; }
h6, h4.htext { font-size: 1.1em !important; }

.article_title {
  color: white !important;
  font-family: "Droid Sans", sans-serif !important;
  padding-left: 40px !important;
}

.articles_content .htext, #article.articles_content .htext {
  margin: 1em 0 !important;
  border-top: 0 !important;
}
.articles_content h2.htext { text-transform: uppercase; }
.articles_content h3.htext { padding: 0.2em 0 !important; }
.articles_content h4.htext, .articles_content h4.htext a, .articles_content h5.htext, .articles_content h5.htext a {
  font-style: italic !important;
  font-weight: normal !important;
}

/* LINKS */

a, a:hover, .inbox a, .inbox a:visited, .postright a:visited, .blocktable a:visited, #punmovepost a, #punmovepost a:visited, #article a {
  color: #608baa !important;
  border-bottom: 1px dotted #608baa !important;
  text-decoration: none !important;
}
a:visited, .pun a:visited {
  color: #709bdd !important;
}
a:hover, .inbox a:hover, #punmovepost a:hover, #article a:hover {
  color: #aaa !important;
  border-bottom: 1px dotted #aaa !important;
}
a.link_nav_anchor, a.nav_edit {
  padding-left: 22px !important;
  color: #9fbc00 !important;
}
a.link_nav_anchor:hover, a.nav_edit:hover { color: #aaa !important; }

#article h2 a, .htext a, #footer a, .add_content a, a.link_nav_anchor, a.nav_edit, .toc a, #menu_content a, #menu_content a:hover, #log a, #log a:hover, .latest li a, #data_section_container a, a.qck_sw, .image a, #hoverNav a, #imageDataContainer a, .box a, #vf a, .postquote a, .postquote a:visited, .postreport a, .postreport a:visited {
  border: 0 !important;
}

/* MISC */

span.hextra, .article span.hextra {
  background-color: #242424 !important;
  border-color: #343434 !important;
  margin-left: 1em !important;
  font-style: normal !important;
}
span.hextra:before { content : "("; }
span.hextra:after { content : ")"; }

.switch_lang {
  position: relative;
  top: -1ex;
  float: right;
  right: 1em;
}
.switch_lang a, div.current_lang {
  width: 2ex;
  overflow: hidden;
  display: inline-block !important;
}
div.current_lang {
  font-weight: normal !important;
  color: #0771a6 !important;
}

/* HEADER */

#page_header {
  background: none repeat scroll 0 0 #333333 !important;
  border-bottom: 5px solid #0088CC !important;
}

#header {
  position: relative;
  height: 70px !important;
}

#banner_logo {
  background: transparent !important;
  top: 10px !important;
  left: 0 !important;
  text-transform: uppercase;
  font-weight: bold;
  font-size: 1.4em;
  height: auto !important;
  width: auto !important;
}
#banner_logo:before {
  content : "Camptocamp";
  color: #f90; font-size: 2em;
}
#banner_logo:after {
  content : ".org";
}

#banner_middle {
  background: transparent !important;
  margin: 0 !important;
  height: auto !important;
  position: absolute !important;
  top: 0.5em;
  right: 0;
  font-size: 0.95em !important;
  z-index: 1 !important;
}

#log > div { float: right; padding-left: 20px !important; }
#log > form { position: absolute; right: 0; top: 30px; }

.sbox, .sbox select, .sbox input {
  background-color: #242424 !important;
  border: 0 !important;
}
.sbox {
  border-color: #343434 !important;
}

a.logged_as { background: transparent !important; padding: 0 !important; }
.log_elt .action_cancel { background: transparent !important; height: auto !important; }
.log_elt .action_cancel:after { content : "✘"; }

/* MENU */

#menu { margin-bottom: 10px !important; }
#menu_up { position: relative; }

#menu_up, #menu_content li, #menu_content ul ul, #menu_content span {
  background: transparent !important;
  border: 0 !important;
}

div#quick_switch {
  left: auto !important;
  right: 0 !important;
  padding: 0 !important;
}

#menu_content { padding: 0 !important; color: #f90 !important; }
#menu_content li { padding-right: 1ex !important; height: 2.5em !important; }
#menu_content ul ul {
  padding: 0 !important;
  margin-left: 0 !important;
  width: 100% !important;
  height: 2em;
  left: 0;
  overflow: hidden;
}
#menu_content li li {
  padding: 0 !important;
  display: inline-block !important;
  height: 1.7em;
  overflow: hidden;
}
#menu_content a, #menu_content li li a, #menu_content li li a:hover {
  color:#f90 !important;
  background: transparent !important;
  height: auto !important;
}
#menu_content ul li a {
  text-transform: uppercase !important;
}
#menu_content li li a {
  padding: 0 0 0 24px !important;
  height: auto !important;
  line-height: 26px !important;
  font-weight: normal !important;
  text-transform: none !important;
}
#menu_content li:hover, #menu_content li:hover a, #menu_content li a:hover {
  color: #608BAA !important;
}

/* HOME */

.home_article a.picto_rss, .home_article span.picto_rss, #last_images_section_title, .home_link_list a[href*="/forums/?lang="], .home_link_list a[href$="feed.rss"], .home_link_list a[href$="/articles/107228/fr"], .home_link_list a[href$="/users/customize"] {
  display: none !important;
}

.content_article .home_article { padding: 0 0 150px !important; }
#home_left_content { width: 65% !important; }
#home_right_content { width: 33% !important; }

.home_title {
  height: auto !important;
  background-color: transparent !important;
}
.home_title_text {
  padding: 5px !important;
  margin: 0 5px 0 5px !important;
  font-family: "Droid serif",serif !important;
  font-size: 1.6em !important;
}

.home_title_text a, .home_title_right a { border-bottom: 0 !important; }

.home_link_list { display: inline !important; margin: 0 !important; }
.home_link_list a { font-size: 12px; padding: 2px 3px !important; }

.home_title_left {
  margin: 8px 8px 0 5px !important;
  top: 2px !important;
  background-color: transparent !important;
}

/* #last_images { position: absolute; bottom: 0; margin: 0 auto !important; } */
#last_images #image_list { height: 145px !important; }
#last_images_section_container { margin-bottom: 0 !important; }

#on_the_web_section_container { display: block !important; }

ul.listed_changes li, ul.children_docs li { list-style-image: none !important; }
ul.dated_changes li.even, ul.listed_changes li.even, ul.article_changes li.even,
ul.docs_changes li.even { background-color: transparent !important; }

.dated_changes .meta a { text-decoration: none !important; border-bottom: 0 !important; }

/* SIDEBAR */

.nav_box {
  width: 17% !important;
  padding: 10px !important;
  margin: 5px !important;
}
.nav_box_content {
  background: transparent !important;
  width: 100% !important;
  border: 0 !important;
}
.nav_box_text, .nav_box_title { margin: 0 !important; padding: 0 !important; }
.nav_box_bottom_link { text-align: left !important; margin: 0 !important; }
.nav_box_content a { font-weight: normal !important; }
.nav_box_image { float: left; }
.nav_box_title_text { margin: 0 0 0 20px !important; }

/* TOOLBAR */

#nav_tools {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 50;
  width: 200px !important;
  height: 10px !important;
  margin: 0 !important;
  padding: 0 !important;
  background-color: #608baa !important;
}
#nav_tools:hover {
  background-color: #242424 !important;
  border-color: #343434 !important;
  height: 350px !important;
}
#nav_tools_content { background-color: transparent !important; }
#nav_tools ul { position: absolute; top: -10000px; }
#nav_tools:hover ul { position: absolute; top: 0; }
#nav_tools ul li { width: 180px !important; display: block !important; }

/* DOC EDIT */

#nav_edit {
  font-size: 11px;
  margin: 0 !important;
  padding: 2px !important;
  width: auto !important;
  position: absolute;
  right: 0;
  top: 0;
}

#nav_edit li, #nav_edit li.active_tab, #nav_edit li:hover {
  display: inline-block;
  padding: 2px 8px !important;
  margin-left: 5px !important;
  background-color: #242424;
  border: 0 !important;
  border-width: 0 1px 1px !important;
  width: auto !important;
}

#nav_edit li a, #nav_edit li div {
  font-weight: normal !important;
  color: #aaa !important;
  border-bottom: 0 !important;
  margin: 0 !important;
  font-size: 11px !important;
}
#nav_edit li a:hover { border: 0 !important; }
#nav_edit li.active_tab a { color: #0088cc !important; font-weight: bold !important; }
#nav_edit li a:link, #nav_edit li a:visited, #nav_edit li div {
  background: transparent !important;
  display: inline !important;
  padding: 0 !important;
}
#nav_edit li a.action_comment { font-size: 11px !important; }

/* DIFF */

table.diff, table.diff_metas { background-color: #343434 !important; }
td.diff-context { background-color: transparent !important; }
table.diff caption { border-bottom: 0; }

/* TOPO */

.title, .title a {
  margin-left: 0 !important;
  font-size: 1.2em !important;
  font-weight: normal !important;
  border-bottom: 0 !important;
}
#content_article .section_subtitle, #article_infos_titre, .article_infos_titre {
  font-weight: normal !important;
  z-index: 1 !important;
}
.article_contenu > .section_subtitle { font-size: 1.4em !important; }
#license_box { padding: 0 !important; margin: 1em 0 4em !important; }
.article .linkst { float: none !important; }
#article_infos_titre_contenu, .article_infos_titre_contenu { text-align: left !important; }
#data_section_container div.all_associations, #data_section_container ul.data { margin: 0 !important; }
#data_section_container { padding: 1em 0 !important; }
#conditions_levels_table td { background-color: transparent !important; border: 2px solid #343434 !important; }

/* MAPS */

.x-border-layout-ct, .x-layout-collapsed, .x-tree .x-panel-body, .x-tree-node, .x-tree-node a, .x-dd-drag-ghost a, .x-tree-node a span, .x-dd-drag-ghost a span, .x-tree-node-over {
  background-color: #141414 !important;
  color: #aaa !important;
}

/* LISTS */

table.list, table#pagehistory { border: 1px solid #343434 !important; }
tr.table_list_odd, tr.table_list_even { background-color: transparent !important; }
table.list th, table#pagehistory th { background-color: #343434 !important; }

/* ARTICLES */

table.toc {
  background-color: #141414 !important;
  border: 1px solid #343434 !important;
  float: right !important;
  margin: 1em !important;
  position: relative !important;
  z-index: 100 !important;
  width: auto;
  max-width: 400px;
}

/* ------- PUNBB --------- */

.pun, .pun optgroup {
  font-family: "Droid Sans", sans-serif !important;
}

#brdwelcome, div.block, div.blockform, div.block2col, #postreview, div.blocktable, div.inbox, .conl, .conr, div.linkst, div.linksb, div.postlinkst {
  margin: 0 !important;
  padding: 0 !important;
}

/*.postleft {
  width: 100% !important;
  }

.postleft dd.usertitle {
display: block !important;
width: 100% !important;
}

.postleft dd.postavatar {
display: block !important;
float: right !important;
}

.postleft dd.postavatar img {
position: absolute;
right: 0;
top: 0;
}

.postright, .postfootright {
border-left: 0 !important;
}*/

td.tcl, td.tcr, #brdwelcome .box, #idx1>.box, #idx2>.box, #idx3>.box, #idx4>.box, #brdstats.block>.box, #brdfooter.block>.box, #vf.blocktable>.box, .box>form, .rowodd>.box>.inbox, .roweven>.box>.inbox, .roweven>.box, .rowodd>.box, h2, .fakeform, #searchform.blockform>.box, .pun>.blockform>.box, #users1.blocktable>.box,.block2col >.blockmenu >.box, .rowodd, .roweven, #postpreview.blockpost>.box, #punredirect.pun>.block>.box>.inbox, #punredirect.pun>.block>.box, #helpfile>.box, div.box {
  background-color: #242424 !important;
  border-color: #343434 !important;
}

th, th.tcl, th.tcr, .tc2, .tc3, blockquote, .codebox, .infldset>dl>dd {
  background-color: #242424 !important;
  border-color: #343434 !important;
}

.tcl, .tc3, .tc2, .tcr, dd, dd>a:hover, .tcr>a:hover, h3>a:hover, .conr>li>a:hover, .conl>li, .linkst>.inbox>ul>li>a:hover, .pagelink>a:hover,  .tclcon>a:hover, .tclcon>strong>a:hover, .postlink>a:hover, .conr>li>a:hover, .subscribelink>a:hover, .postmsg>p, .incqbox, dt>strong>a:hover,  .postmsg>p>a:hover, .postquote>a:hover, .bblinks>li>a:hover, .incqbox>p>a:hover, .infldset>dl>dt, .clearb, .conl, .infldset, #profile1>p, .isactive>a, .postleft>dl>dt>strong, .linkst>.inbox>ul>li, .pun select, .postlinksb>.inbox>ul>li, .postfootleft>p, #punredirect.pun>.block>.box>.inbox>p, #punredirect.pun>.block>.box>.inbox>p>a:hover, .newtext>a:hover, .tc2>a:hover, .postlinksb>.inbox>ul>li>a:hover, #helpfile>.box, .postedit>a:hover {
  color: #aaa !important;
}

.postsignature, .postsignature a, .postsignature>strong>a:hover, .postsignature>a:hover {
  color: #666 !important;
}
.postleft, .postfootleft, .blockpost label, .pun select {
  background-color: #343434 !important;
}
.postright, .postfootright {
  background-color: #242424 !important;
  border-color: #343434 !important;
}

.icon{ border-color: #999999 !important; }
.inew{ border-color: #0771a6 !important; }
.new h2 span.conr, .new h2 a, .new h2 a:visited { color: #9FBC00 !important; }
.tclcon strong a, .postleft strong a { color:#9fbc00 !important; }
#brdheader a { color: white !important; }
.blockpost h2 { background: #1a1a1a !important; }
#punviewtopic div.box { border: 1px solid #343434 !important; }
.forum_desc { background: transparent !important; }
.inbox li.pmlink a { color: #f90 !important; }

.pun h2, #brdmenu {
  background: transparent !important;
  color: #aaa !important;
  margin: 1em 0 0.5em !important;
  padding: 0 !important;
}

/* FOOTER */

#footer {
  margin: 1em auto !important;
  padding: 10px !important;
  height: auto !important;
  font-size: 10px !important;
  left: auto !important;
  background-image: none !important;
}
#footer_cc { margin: 0 !important; padding: 0 !important; position: static !important; }
#footer_cc_text { width: 60% !important; }
#footer_cc_text > p+p { display: inline !important; }
#footer_cc_text > p+p:after { content : " | "; }

#footer_partners {
  width: 35% !important;
  right: 10px !important;
  left: auto !important;
  text-align: right;
}
#partners { display: inline !important; }
#partners li {
  display: inline-block !important;
  float: none !important;
  margin: 0 5px !important;
}

/* LOGIN */

#MB_window {
  position: absolute !important;
  top: 0 !important;
  bottom: 0 !important;
  left: 0 !important;
  right: 0 !important;
  margin: auto !important;
}
#MB_header, #MB_caption, #MB_content {
  background-color: #242424 !important;
  color: #aaa !important;
  border: 0 !important;
}

/* CHANGER D'APPROCHE */

#cda_logo { margin: 35px 0 0 !important; }
#cda_title { margin: 20px 0 0 !important; }
#cda_sub_title { margin: 0 0 10px !important; }
#c2c_mw_logos { display: none !important; }

}
