@namespace url(http://www.w3.org/1999/xhtml);

/* Wikipedia DeepPurple Molecular Sidebar */
/* Xavian-Anderson Macpherson */
/* Shingoshi@Gmail.com */
/* NASA Night Launch Specific Modifications */
/* https://addons.mozilla.org/en-US/firefox/addon/4908 */


@-moz-document domain("wikipedia.org"),
domain("wiktionary.org"),
domain("wikibooks.org"),
domain("wikiversity.org"),
domain("wikispecies.org"),
domain("wikisource.org"),
domain("wikinews.org"),
domain("wikiquote.org"),
domain("wikimedia.org")

 {

/*=============================================*/
/* Links & Text */
#bodyContent h1,
#bodyContent h2,
#toc h2,

.portlet h5,
.toc h2,

a,
h1, h2, h3, h4, h5, h6
 {
 color: #ccf !important;
 }

/* Link Backgrounds */
#p-cactions li a,
#p-cactions li a:hover,

.ns-0 * #p-cactions li a,
.ns-0 * #p-cactions li.selected a,
.portlet a,
.portlet li

 {
 color: #ccf !important;
 background-color: #000 !important;
 }

/* Active Hover */
a:hover
 {
 color: #5f5 !important;
 font-weight: bolder !important;
 text-decoration: underline !important;
 }
/*=============================================*/
i,
p,
span,
table
 {
 color: #bbb !important;
 }
/*=============================================*/


/*=============================================*/
/* Wiki DeepBlue Right Sidebar - td */
.gallerybox .thumb img,
.infobox,

div.thumb,
div.thumb div a img,
pre,
table.gallery,
table.gallery td,
td,

[class="gallery"],
[class="tex"],
[class="thumb"],
[class="thumbimage"]
 {
 background-color: #313 !important;
 border-color: #000 !important;
 }
/*=============================================*/


/*=============================================*/
img
 {
 background-color: #eae !important;
 }
/*=============================================*/


/*=============================================*/
/* Deepest Purple */
div#userloginForm form,
div#userloginForm form#userlogin2,
div#userloginForm table,
div#userloginForm form#userlogin2 table,
table.navbox,
th,

[class="catlinks"],
[class="pBody"],
[class='plainlinks'],
[class="portlet"],
[class="thumbinner"],

[id="catlinks"],
[id="column-content"],
[id="toc"]
 {
 background-color: #101 !important;
 }
/*=============================================*/


/*=============================================*/
/* BlackGrounds */
body,
cite,
html,
input,
li,
select,

[class="firstHeading"],
[class="mw-headline"],
[class="selflink"],
[class="toccolours"],
[class="tright"],
[class="wikitable"],

[id="ca-edit"],
[id="ca-history"],
[id="ca-talk"],
[id="content"],
[id="footer"],
[id="siteSub"]
 {
 color: #aaa !important;
 background-color: #000 !important;
 }
/*=============================================*/


/*=============================================*/
/* WikiPedia Icon */
[style="background-image: url(/images/wiki-en.png);"]
{
	background-image: url("data:image/png;base64,") !important;
}


}
