css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.poneyvallee.com/forum/"), url-prefix("http://www.poneyvallee.com/forum2/") {

body {
	background : #E0A0D2 url("http://www.poneyvallee.com/design/wall_anniversaire10.jpg") center fixed repeat !important;
}	

#brdheader {background : url("http://nsm08.casimages.com/img/2014/10/15//14101510120110339312616417.png") ;}	
	
img[src*="../images/accueil/pv_titre.png"] {
	background: url("http://nsm08.casimages.com/img/2014/10/15//14101510434010339312616569.png") no-repeat ;
	width : 0 ;
	height : 0 ;
	padding-top : 151px ;
	padding-left : 472px ;
	margin-left : 175px ;

}
	
#brdmenu[style*="width"] {
	background: -moz-linear-gradient(top, #E960B2, #E4088C, #E960B2) !important;
	margin-left : 5px !important ;
	width : 780px !important ;
	height : 25px !important ;
}
#brdmenu a{
	color : #FEFCAF !important;
	border-bottom: none !important;
}
#brdmenu a:hover{
	color : white !important;
	border-bottom: none !important;
}
	
#brdmenu+div {
	background : none !important ;
	color : white ;
}
#brdmenu+div a{
	color : white ;
	border-bottom : 1px dotted white ;
}	
	
h2 {
	background: #E5319E !important;
	color : white !important ;
	border : 2px #E5319E solid !important;
	border-bottom : none !important ;
}	

thead th, .arrondis_bas {
	background: linear-gradient(to bottom, #E5319E,#FEFCAF) !important;
	color : black !important;
	font-weight : bold !important ;
}	
	
.arrondis_bas, .blocktable tbody {
	background: #FEFCAF !important;
	border : #E5319E 2px dashed !important ;
	border-top : none !important ;
	color : #C77627 !important;
}	
	
td.tc2, td.tc3 {background : none !important ;}

thead th a, .arrondis_bas a{
	color : #8A331F !important;
}
	
.tclcon p a {
	border-bottom : 1px dashed #C77627 !important;
}
	
tbody td, thead th {
	border : none !important ;
	border-top : 1px solid #DF9FD1 !important ;
}	
	
tbody h3 a {
	border-bottom : none !important ;
}	
	
.tc2 {
	border-left : 1px dotted #DF9FD1 !important ;
	border-right : 1px dotted #DF9FD1 !important ;
}
.tc3 {
	border-right : 1px dotted #DF9FD1 !important ;
}


#brdstats .box, #brdfooter .box{
	background: url("http://nsm08.casimages.com/img/2014/10/15//14101510120110339312616417.png") repeat !important;
	border : white 1px dotted !important ;
	border-radius : 5px !important;
	color : white !important ;
}
#brdstats .box a, #brdfooter .box a{
	color : #994291 !important ;
	border-bottom : #994293 1px dashed !important ;
}


.icon.inew {
	background:transparent url("http://nsm08.casimages.com/img/2014/10/15//14101510120010339312616416.png") right no-repeat !important;
	background-size: 30px 30px !important; 
	height : 30px !important; 
	margin-right : 10px  !important; 
}

.tcr a {
	border-bottom : #C77627 1px dotted !important ;
}

.blockpost>.box {
	background: #FEFCAF !important;
	border-top : none !important ;
	border : 2px #E5319E solid !important ;
	border-left : none !important ;
}
	
	
.postleft, .postfootleft {background : url("http://nsm08.casimages.com/img/2014/10/15//14101510120110339312616417.png") !important ;}		
.postright, .postfootright {
	background : transparent !important ;
	border-color : transparent !important ;
	border-image : url("http://nsm08.casimages.com/img/2014/10/15//14101510120110339312616417.png") 1 220 !important ;
}

.blockform>.box {
	background: #FEFCAF !important ;
	border-top : none !important ;
}

.postmsg a {
	color : #71003B !important;
	border-bottom: 1px dotted #71003B !important;
}
.postmsg a:hover {
	color : black !important;
	border-bottom: 1px dotted black !important;
}
.usertitle {
	color : #FEFCAF !important;
}
.postleft dt a{
	color : black !important;
	border-bottom: none !important;
}
.usercontacts a{
	color : #503551 !important;
	border-bottom: 1px dashed #503551 !important;
}
.linkst a{
	color : white !important;
	border-bottom: 1px dashed white !important;
}

DIV.postright {
	border-left: solid #FF6DC7 198px !important;
}

.blockpost a {
	color : black !important ;
	border-bottom : 2px dotted white !important ;
}


}
