css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
#overhead{background:transparent -moz-linear-gradient(rgba(52,62, 60, 0.7), rgba(158, 177, 162, 0.7))!important;position:fixed!important;width:100%!important;height:26px!important;}

#overhead-collect{background:transparent none!important;border-bottom-width:0!important;}

body{padding-top:26px!important;border-width:0!important;background: transparent none!important;}

html{background:#000 url(http://fc03.deviantart.net/fs71/f/2010/032/d/a/Deep_Space_Nebula_by_hameed.jpg) fixed top right no-repeat!important;} 

.catbar{border-top-width:0!important;}

.oh-l {height:20px!important;padding: 9px 20px 0px 5px!important;}

.mmhover .oh-l{background-color:rgba(74, 92, 80, 0.6)!important;}



.oh-hasmenu h1{padding:0!important;top:-8px!important;height:0px!important;width:0px!important;background-image:url(http://trezoid.webs.com/dAfiles/Headerimage.png)!important;left:0px!important;position:fixed!important;padding-right:127px!important; padding-bottom:37px!important;}

.oh-menu{margin-top:29px!important;top:0px!important;background:rgba(74, 92, 80, 0.88) none!important;}

.oh-l i{display:none!important;}

#oh-mainmenu{left:0px!important;position:fixed!important; top:3px!important;}

#oh-more{left:0px!important; width:140px!important; background:transparent none!important;}

.oh-l-green{position:fixed!important; right:0!important;background: rgba(74, 92, 80, 0.7) none!important;-moz-border-radius-bottomleft:5px!important;height:26px!important;-moz-box-shadow:0 0 5px #000!important;}

#search7-ctrl{margin-top:0!important;}

#gruze-columns, #deviant, #output, #gmi-GPage, #gmi-Gruser{background:transparent none!important;}

.gr-box i.tri, .gr1, .gr2, .gr3{display:none!important;}

.gr-top, .ch-top, #lit-view{-moz-border-radius-topleft:5px!important; -moz-border-radius-topright:5px!important;border-width:0!important;background:-moz-linear-gradient(rgba(202, 215, 198, 0.6), rgba(158, 177, 162, 0.6))!important;-moz-box-shadow:0px 3px 5px rgba(44, 54, 53, 0.5)!important;}

.gr-top .gr{background:transparent none!important;border-width:0!important;}

.gr-body, .browse2 td:first-of-type, #ddtable tr, .page2, .talkmessage, .text, .ppp{background: rgba(114, 135, 118, 0.6)!important;-moz-border-radius-bottomleft:5px!important;-moz-border-radius-bottomright:5px!important;border-width:0!important;}

.blues-bar{background:transparent none!important;border-width:0!important;}

.blues-bar-ctrl, #facetMenu-Category_list li:hover a, #facetMenu-Browse li:hover a, #facetMenu-Focus_list li:hover a, #dailyDevTopNav, #dailyDevBottomNav, .bubbletop, .header{background:-moz-linear-gradient(rgba(202, 215, 198, 0.6), rgba(158, 177, 162, 0.6))!important;}

.gtab-i{display:none!important;}

.gtabi-mc{border-bottom-width:0!important;padding-right:10px!important;-moz-border-radius-topleft:5px!important; -moz-border-radius-topright:5px!important;top:0!important;background:-moz-linear-gradient(rgba(114, 135, 118, 0.4), rgba(202, 215, 198, 0.4))!important; }

.catbar, tr.alt{background: rgba(114, 135, 118, 0.6)!important;}

.gruserbadge *{color:#9eb1a2!important;}

.ctext, .cc-in, .list .a, #depths, .mczone-title, .mc-ctrl, .ch-ctrl, .altaltview, .talkmessage-comment, .previewbox, .fora .f{background: rgba(114, 135, 118, 0.6)!important;-moz-box-shadow:3px 3px 5px rgba(44, 54, 53,0.5)!important;}


i.l{display:none!important;}

.gr-twitter *{color: #FEEDF8!important;}

a{color:#E03168!important;}
a:hover{color:  #BE4762!important;}

/*other pages*/

#dv7, #browse2-results, #browse2, #browse2-stream, .browse2-results, .browse2-right, body, .btd-browse, .browse2{background:transparent none!important;}

#artist-comments{padding:10px!important; padding-left:65px!important; margin-left:0!important; background:  rgba(114, 135, 118, 0.6)!important;-moz-border-radius:5px!important;-moz-box-shadow:0px 3px 5px rgba(44, 54, 53, 0.5)!important;}

#artist-comments .gr-body {background: transparent none!important;}

.critique-plate .gr-body .gr, #dailyDevs, #gmi-ResourcePageDisplayPane{background:transparent none!important;border-top:0!important;}

.critique-plate .gr-body{-moz-border-radius:5px!important;}

#dailyDevTopNav{margin-top:-7px!important;border-width:0!important;}

#dailyDevBottomNav{border-top-width:0!important;}

.iehh{margin-top:-12px!important;}

.f .text, .details-section, small{color:#cad7c6!important;}

.pager2, .messages, .messages-left,  #messages .f, .messages, .mczone, .messages-right, .f, .bubbleview{background:transparent none!important;}

.mczone-loading, #fake-col-left, .cctextarea{background:transparent none!important; border-width:0!important;}
.mczone-title, .mczone{border-width:0!important;color: #FEEDF8!important;}

.mm, .dd{display:none!important;}

.inner .text{background:transparent none!important;}

#download-button small{color:#101010!important;}

.fora .f{border-width:1px!important;-moz-border-radius:0!important;}

/*v1.3 fixes*/
.gmbutton2header, .oh-long *{top:3px!important;}

.oh-long{padding-top:9px!important;}

#deviant, #output{margin-top:3px!important;}

.oh-vsep, #friendslink, .collectmenu{display:none!important;}
}
