css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.flashback.org") {

/* *************
 * BEIGN THEME *
 ************* */

A {
   font-family: verdana, arial, sans serif !important;
}

body {
   background: #d9d1b7 !important;
}

#site {
   margin-top: 5px !important;
   margin-bottom: 5px !important;
   background: #eff0f1 !important;
   border: 1px solid #777777 !important;
   -moz-border-radius: 7px !important;
}

.list-forum-title {
   margin-top: 0 !important;
   margin-bottom: 0 !important;
   padding: 0 !important;
   border: 0 !important;
}

.lft-color {
   display: none !important;
}

.tcat {
   margin-top: 0 !important;
   margin-bottom: 0 !important;
   padding: 0 !important;
   background: #999999 !important;
   border: 1px solid #7a7a7a !important;
   font-family: verdana, arial, sans serif !important;
}

.normal {
   font-family: verdana, arial, sans serif !important;
}

#top {
   border-top: 1px solid #777777 !important;
   border-bottom: 1px solid #777777 !important;
}

#top-menu {
   border-bottom: 1px solid #777777 !important;
}

#top-tabs {
   border: 0 !important;
   background: 0 !important;
}

#top-tabs > LI {
   font-size: 9px !important;
   padding: 0 !important;
   background: #999999 !important;
   border: 1px solid #7a7a7a !important;
}

#top-tabs > LI A {
   font-weight: bold !important;
}

#top-tabs-icons {
   padding-left: 3px !important;
   margin-top: 2px !important;
}

TD[style="padding: 0px 4px;"] {
   padding: 0px 3px !important;
}

#site-left {
   background: #eff0f1 !important;
}

#site-right {
   padding-top: 0px !important;
   background: #eff0f1 !important;
}

.box-right {
   width: 218px !important;
   margin-top: 0px !important;
   margin-bottom: 5px !important;
}

.alt2 {
   background: #e3e3e3 !important;
}

.alignc.p4.post {
   border: 1px solid #777777 !important;
}

.thead.alignr {
   height: 23px !important;
   background: url(data:image/gif;base64,R0lGODlhAQAXAMQAAAAAAP////z8/OHh4eDg4N/f397e3t3d3dzc3NnZ2djY2NTU1NPT09HR0dDQ0M7Ozs3NzcrKysPDw6SkpP///wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABQALAAAAAABABcAAAUToCQUTHIMRIEoS/NAUQQ5jGJMIQA7) !important;
}

.thead.post-date {
   height: 23px !important;
   background: url(data:image/gif;base64,R0lGODlhAQAXAMQAAAAAAP////z8/OHh4eDg4N/f397e3t3d3dzc3NnZ2djY2NTU1NPT09HR0dDQ0M7Ozs3NzcrKysPDw6SkpP///wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABQALAAAAAABABcAAAUToCQUTHIMRIEoS/NAUQQ5jGJMIQA7) !important;
}

.thead.alignc {
   height: 23px !important;
   background: url(data:image/gif;base64,R0lGODlhAQAXAMQAAAAAAP////z8/OHh4eDg4N/f397e3t3d3dzc3NnZ2djY2NTU1NPT09HR0dDQ0M7Ozs3NzcrKysPDw6SkpP///wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABQALAAAAAABABcAAAUToCQUTHIMRIEoS/NAUQQ5jGJMIQA7) !important;
}

.thead.td_status {
   height: 23px !important;
   background: url(data:image/gif;base64,R0lGODlhAQAXAMQAAAAAAP////z8/OHh4eDg4N/f397e3t3d3dzc3NnZ2djY2NTU1NPT09HR0dDQ0M7Ozs3NzcrKysPDw6SkpP///wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAABQALAAAAAABABcAAAUToCQUTHIMRIEoS/NAUQQ5jGJMIQA7) !important;
}

.vbmenu_control.smallfont2.delim {
   font-family: verdana, arial, sans serif !important;
}

.top-menu-main A {
   font-size: 9px !important;
}

#footer > DIV:first-child {
   padding-right: 10px !important;
   border-right: 1px solid #cccccc !important;
}

#footer > DIV:last-child {
   text-transform: uppercase !important;
   padding-top: 0px !important;
   font-size: 13px !important;
   padding-left: 10px !important;
   float: left !important;
}
   
#footer > DIV:last-child > a:hover {
   text-decoration: underline !important;
}

#footer {
   margin-top: 0px !important;
   border: none !important;
}

/* ********
 * IMAGES *
 ******** */

IMG[src$="https://static.flashback.info/img/buttons-new/collapse_tcat.gif"] {
   height: 17px !important;
   width: 0 !important;
   padding-left: 17px !important;
   background: url(data:image/gif;base64,R0lGODlhEQARAKIAAAAAAP///5wAAJmZmWZmZv///wAAAAAAACH5BAEAAAUALAAAAAARABEAAANIOEPb/G6ASatdQOm9ActCKI6ChA1kKn1oGLzBaBKg+5KrKcKxONeCGMzn0ZFuoV/rSGSleskMbZmKOqvEE2ebs3grEYi4MUgAADs=) !important;
}

IMG[src$="https://static.flashback.info/img/buttons-new/collapse_tcat_collapsed.gif"] {
   height: 17px !important;
   width: 0 !important;
   padding-left: 17px !important;
   background: url(data:image/gif;base64,R0lGODlhEQARAKIAAAAAAP///5wAAJmZmWZmZv///wAAAAAAACH5BAEAAAUALAAAAAARABEAAANJSKO83mOASatdIOq9gcpCKI6ChA1kKn0oGYwmAaZvuJp0XWZyK7yBoOg2+wV1u5PoSCL6jshYMWXz4KhDXobDJVq+FE1j/FgkAAA7) !important;
}

.icon-post-quote {
   background: url(data:image/gif;base64,) no-repeat !important;
   height: 15px !important;
   width: 49px !important;
}
.icon-post-quotem {
   background: url(data:image/gif;base64,) no-repeat !important;
   height: 15px !important;
   width: 72px !important;
}

.icon-post-report {
   background: url(data:image/gif;base64,) no-repeat !important;
   height: 15px !important;
   width: 66px !important;
}
.icon-post-edit {
   background: url(data:image/gif;base64,) no-repeat !important;
   height: 15px !important;
   width: 47px !important;
}

.icon-post-reply, .icon-post-reply-small {
   background: url(data:image/gif;base64,) no-repeat !important;
   height: 15px !important;
   width: 43px !important;
}

.icon-post-forward {
   background: url(data:image/gif;base64,) no-repeat !important;
   height: 15px !important;
   width: 91px !important;
}

/* ***********
 * END THEME *
 *********** */

}
